INSERT INTO `posts` (`idtin`, `title`, `content`, `user_id`, `is_public`, `createdate`, `updatedate`, `idnhom`, `anhtrichdan`, `videotrichdan`, `noidungtrichdan`) VALUES
(97, 'NGUYÊN TẮC CHIA ĐỘNG TỪ ', '<p style=\"margin-left:31px\"><span style=\"font-size:13pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"color:black\"><span style=\"font-size:24.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">1.QUY TẮC TH&Ecirc;M -S, -ES V&Agrave;O SAU ĐỘNG TỪ:</span></span> </span></span></span></p>\r\n\r\n<ul>\r\n	<li style=\"list-style-type:none\">\r\n	<ul>\r\n		<li><span style=\"font-size:13pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"color:black\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">Th&ecirc;m S v&agrave;o sau c&aacute;c từ b&igrave;nh thường: work &ndash; works. </span></span></span></span></li>\r\n		<li><span style=\"font-size:13pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"color:black\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">Th&ecirc;m ES v&agrave;o sau c&aacute;c động từ tận c&ugrave;ng bằng S, O, X, SH, CH: teach &ndash; teaches. </span></span></span></span></li>\r\n		<li><span style=\"font-size:13pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"color:black\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">C&aacute;c động từ tận c&ugrave;ng bằng Y, nếu trước Y l&agrave; phụ &acirc;m th&igrave; ta đổi Y th&agrave;nh I rồi th&ecirc;m ES, nếu trước Y l&agrave; nguy&ecirc;n &acirc;m th&igrave; chỉ cần th&ecirc;m S: study &ndash; studies; nhưng play &ndash; plays. </span></span></span></span></li>\r\n	</ul>\r\n	</li>\r\n</ul>\r\n\r\n<p style=\"margin-left:57px\"><span style=\"font-size:13pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"color:black\">&nbsp;</span></span></span></p>\r\n\r\n<p style=\"margin-left:31px\"><span style=\"font-size:13pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"color:black\"><span style=\"font-size:24.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">2.QUY TẮC TH&Ecirc;M -ED V&Agrave;O SAU ĐỘNG TỪ: </span></span></span></span></span></p>\r\n\r\n<ul>\r\n	<li style=\"list-style-type:none\">\r\n	<ul>\r\n		<li><span style=\"font-size:13pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"color:black\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">Th&ecirc;m ED v&agrave;o sau c&aacute;c động từ b&igrave;nh thường: play &ndash; played. </span></span></span></span></li>\r\n		<li><span style=\"font-size:13pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"color:black\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">C&aacute;c động từ tận c&ugrave;ng bằng E&nbsp; </span></span></span></span></li>\r\n		<li><span style=\"font-size:13pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"color:black\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">c&acirc;m th&igrave; chỉ cần th&ecirc;m D: die &ndash; died.</span> </span></span></span></li>\r\n		<li><span style=\"font-size:13pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"color:black\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\"><span style=\"color:#333333\">Những động từ c&oacute; 1 &acirc;m tiết, tận c&ugrave;ng bằng 1 phụ &acirc;m (trừ H, W, X, Y) v&agrave; trước đ&oacute; l&agrave; 1 nguy&ecirc;n &acirc;m th&igrave; nh&acirc;n đ&ocirc;i phụ &acirc;m cuối rồi mới th&ecirc;m ED: stop &ndash; stopped, nhưng stay &ndash; stayed. </span></span></span></span></span></li>\r\n		<li><span style=\"font-size:13pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"color:black\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\"><span style=\"color:#333333\">Những động từ c&oacute; 2 &acirc;m tiết, trọng &acirc;m nằm ở &acirc;m cuối, tận c&ugrave;ng l&agrave; 1 phụ &acirc;m (trừ H, W, X, Y) v&agrave; trước đ&oacute; l&agrave; 1 nguy&ecirc;n &acirc;m th&igrave; nh&acirc;n đ&ocirc;i phụ &acirc;m cuối rồi mới th&ecirc;m ED: prefer &ndash; preferred. </span></span></span></span></span></li>\r\n		<li><span style=\"font-size:13pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"color:black\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\"><span style=\"color:#333333\">C&aacute;c động từ tận c&ugrave;ng bằng Y, nếu trước Y l&agrave; phụ &acirc;m th&igrave; ta đổi Y th&agrave;nh I rồi th&ecirc;m ED, nếu trước y l&agrave; nguy&ecirc;n &acirc;m th&igrave; chỉ cần th&ecirc;m ED: study &ndash; studied, nhưng play &ndash; played.&nbsp; </span></span></span></span></span></li>\r\n		<li><span style=\"font-size:13pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"color:black\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\"><span style=\"color:#333333\">Một số trường hợp bất quy tắc nằm trong bảng động từ bất quy tắc. </span></span>&nbsp;</span></span></span></li>\r\n	</ul>\r\n	</li>\r\n</ul>\r\n\r\n<p style=\"margin-left:57px\"><span style=\"font-size:13pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"color:black\">&nbsp;</span></span></span></p>\r\n\r\n<p style=\"margin-left:31px\"><span style=\"font-size:13pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"color:black\"><span style=\"font-size:24.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">3.QUY TẮC TH&Ecirc;M -ING V&Agrave;O SAU ĐỘNG TỪ:</span></span> </span></span></span></p>\r\n\r\n<p style=\"margin-left:57px\"><span style=\"font-size:13pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"color:black\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">Th&ecirc;m ING v&agrave;o sau c&aacute;c động từ b&igrave;nh thường: do &ndash; doing. </span></span></span></span></p>\r\n\r\n<ul>\r\n	<li style=\"list-style-type:none\">\r\n	<ul>\r\n		<li><span style=\"font-size:13pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"color:black\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">Những động từ tận c&ugrave;ng bằng 1 chữ E c&acirc;m th&igrave; bỏ E rồi th&ecirc;m ING: write &ndash; writing; trường hợp đặc biệt: singe &ndash; singeing, dye &ndash; dyeing. </span></span></span></span></li>\r\n		<li><span style=\"font-size:13pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"color:black\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">Những động từ c&oacute; 1 &acirc;m tiết, tận c&ugrave;ng bằng 1 phụ &acirc;m (trừ H, W, X, Y) v&agrave; trước đ&oacute; l&agrave; 1 nguy&ecirc;n &acirc;m th&igrave; nh&acirc;n đ&ocirc;i phụ &acirc;m cuối rồi mới th&ecirc;m ING: run &ndash; running, nhưng fix &ndash; fixing. </span></span></span></span></li>\r\n		<li><span style=\"font-size:13pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"color:black\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">Những động từ c&oacute; 2 &acirc;m tiết, trọng &acirc;m nằm ở &acirc;m cuối, tận c&ugrave;ng l&agrave; 1 phụ &acirc;m (trừ H, W, X, Y) v&agrave; trước đ&oacute; l&agrave; 1 nguy&ecirc;n &acirc;m th&igrave; nh&acirc;n đ&ocirc;i phụ &acirc;m cuối rồi mới th&ecirc;m ING: begin &ndash; beginning. </span></span></span></span></li>\r\n		<li><span style=\"font-size:13pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"color:black\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">Những động từ tận c&ugrave;ng bằng IE th&igrave; đổi IE th&agrave;nh Y rồi th&ecirc;m ING: die &ndash; dying. </span></span></span></span></li>\r\n		<li><span style=\"font-size:13pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"color:black\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">C&aacute;c động từ tận c&ugrave;ng bằng C v&agrave; trước C l&agrave; 1 nguy&ecirc;n &acirc;m th&igrave; phải th&ecirc;m K rồi mới th&ecirc;m ING. </span></span></span></span></li>\r\n		<li><span style=\"font-size:13pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"color:black\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">C&aacute;c động từ tận c&ugrave;ng bằng L, trước L l&agrave;1 nguy&ecirc;n &acirc;m th&igrave; c&oacute; thể gấp đ&ocirc;i chữ L (theo c&aacute;ch của Anh) hoặc kh&ocirc;ng (theo c&aacute;ch của Mỹ). travel -&gt; travelling/ traveling.</span> </span></span></span></li>\r\n	</ul>\r\n	</li>\r\n</ul>\r\n', 7, 0, '2018-12-21 14:11:15', '2018-12-21 14:11:15', 6, '', NULL, NULL),
(93, 'SIMPLE PAST (QUÁ KHỨ ĐƠN) ', '<p style=\"margin-left:17px\"><span style=\"font-size:13pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"color:black\"><span style=\"font-size:24.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">I.</span></span><strong> </strong><span style=\"font-size:24.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">CẤU TR&Uacute;C </span></span></span></span></span></p>\r\n\r\n<ul>\r\n	<li><span style=\"font-size:13pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"color:black\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">S + V- ed&nbsp; &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; (+) </span></span></span></span></li>\r\n	<li><span style=\"font-size:13pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"color:black\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">S + didn&rsquo;t + V &nbsp; &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; (-) </span></span></span></span></li>\r\n	<li><span style=\"font-size:13pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"color:black\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">S + wasn&rsquo;t/ weren&rsquo;t/couldn&rsquo;t + adj/V </span></span></span></span></li>\r\n	<li><span style=\"font-size:13pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"color:black\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">Was/Were + S + adj/&hellip;.? &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; (?)&nbsp;&nbsp;&nbsp; </span></span></span></span></li>\r\n</ul>\r\n\r\n<p style=\"margin-left:31px\"><span style=\"font-size:13pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"color:black\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">&rarr;</span> <span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">No, S wasn&rsquo;t/weren&rsquo;t.&nbsp;&nbsp;&nbsp;&nbsp; </span></span></span></span></p>\r\n\r\n<p style=\"margin-left:31px; margin-right:1px\"><span style=\"font-size:13pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"color:black\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">&rarr;</span> <span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">Yes, S was/were. </span></span></span></span></p>\r\n\r\n<ul>\r\n	<li><span style=\"font-size:13pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"color:black\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">Did + S + V?&nbsp; </span></span></span></span></li>\r\n</ul>\r\n\r\n<p style=\"margin-left:31px\"><span style=\"font-size:13pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"color:black\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">&rarr;</span> <span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">No, S didn&rsquo;t. </span></span></span></span></p>\r\n\r\n<p style=\"margin-left:31px; margin-right:1px\"><span style=\"font-size:13pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"color:black\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">&rarr;</span> <span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">Yes, S did. </span></span></span></span></p>\r\n\r\n<p style=\"margin-left:7px\"><span style=\"font-size:13pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"color:black\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">Lưu &yacute; :&nbsp; </span></span></span></span></p>\r\n\r\n<ul>\r\n	<li style=\"list-style-type:none\">\r\n	<ul style=\"list-style-type:circle\">\r\n		<li><span style=\"font-size:13pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"color:black\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">I/ We/ They/ You: Were.&nbsp;&nbsp; </span></span></span></span></li>\r\n		<li><span style=\"font-size:13pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"color:black\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">He/She/It: Was<em> </em></span></span></span></span></li>\r\n	</ul>\r\n	</li>\r\n</ul>\r\n\r\n<p style=\"margin-left:7px; margin-right:1px\"><span style=\"font-size:13pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"color:black\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Khi đổi sang phủ định v&agrave; nghi vấn, nhớ đưa động từ ch&iacute;nh về nguy&ecirc;n mẫu. </span></span></span></span></p>\r\n\r\n<p style=\"margin-left:7px\"><span style=\"font-size:13pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"color:black\">&nbsp;</span></span></span></p>\r\n\r\n<p style=\"margin-left:8px\"><span style=\"font-size:13pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"color:black\"><span style=\"font-size:24.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">II.</span></span><strong> </strong><span style=\"font-size:24.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">C&Aacute;CH D&Ugrave;NG </span></span></span></span></span></p>\r\n\r\n<ul>\r\n	<li><span style=\"font-size:13pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"color:black\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">Th&igrave; qu&aacute; khứ đơn diễn tả h&agrave;nh động trong qu&aacute; khứ, chấm dứt rồi v&agrave; biết r&otilde; thời gian. </span></span></span></span></li>\r\n</ul>\r\n\r\n<p style=\"margin-left:85px; margin-right:1px\"><span style=\"font-size:13pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"color:black\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">Tom <u>went</u> to London last summer. </span></span></span></span></p>\r\n\r\n<p style=\"margin-left:85px; margin-right:1px\"><span style=\"font-size:13pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"color:black\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">&nbsp;He <u>died</u> in 1900. </span></span></span></span></p>\r\n\r\n<ul>\r\n	<li><span style=\"font-size:13pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"color:black\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">Th&igrave; qu&aacute; khứ đơn diễn tả h&agrave;nh động đ&atilde; xảy ra suốt 1 khoảng thời gian trong qu&aacute; khứ, nhưng nay đ&atilde; ho&agrave;n to&agrave;n chấm dứt. </span></span></span></span></li>\r\n</ul>\r\n\r\n<p style=\"margin-left:85px; margin-right:1px\"><span style=\"font-size:13pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"color:black\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">She <u>knew</u> how to swim when she <u>was</u> a child. </span></span></span></span></p>\r\n\r\n<p style=\"margin-left:7px\"><span style=\"font-size:13pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"color:black\">&nbsp;</span></span></span></p>\r\n\r\n<p style=\"margin-left:1px\"><span style=\"font-size:13pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"color:black\"><span style=\"font-size:24.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">III.</span></span><strong> </strong><span style=\"font-size:24.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">DẤU HIỆU NHẬN BIẾT </span></span></span></span></span></p>\r\n\r\n<p style=\"margin-left:31px; margin-right:1px\"><span style=\"font-size:13pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"color:black\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">&bull;</span> <span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">Trong c&acirc;u c&oacute; c&aacute;c từ&nbsp; k&egrave;m theo : </span></span></span></span></p>\r\n\r\n<ul style=\"list-style-type:circle\">\r\n	<li><span style=\"font-size:13pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"color:black\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">Last week/month/year : Tuần trước/ th&aacute;ng trước/ năm trước. </span></span></span></span></li>\r\n	<li><span style=\"font-size:13pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"color:black\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">Yesterday:&nbsp; Ng&agrave;y h&ocirc;m qua </span><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">o</span> <span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">Ago: c&aacute;ch đ&acirc;y (a month ago : 1 th&aacute;ng trước,&hellip;) </span></span></span></span></li>\r\n</ul>\r\n', 7, 0, '2018-12-21 10:34:59', '2018-12-21 10:34:59', 5, '', NULL, NULL),
(94, 'PAST CONTINOUS (QUÁ KHỨ TIẾP DIỄN)', '<p style=\"margin-left:31px\"><span style=\"font-size:13pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"color:black\"><span style=\"font-size:24.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">1.CẤU TR&Uacute;C </span></span></span></span></span></p>\r\n\r\n<ul>\r\n	<li style=\"list-style-type:none\">\r\n	<ul>\r\n		<li><span style=\"font-size:13pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"color:black\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">S + was/ were + V-ing&nbsp; &nbsp;&nbsp;&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; (+) </span></span></span></span></li>\r\n		<li><span style=\"font-size:13pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"color:black\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">S + wasn&rsquo;t/ weren&rsquo;t + V-ing (-) </span></span></span></span></li>\r\n		<li><span style=\"font-size:13pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"color:black\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">Was/were + S + V-ing?&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; (?) </span></span></span></span></li>\r\n	</ul>\r\n	</li>\r\n</ul>\r\n\r\n<p style=\"margin-left:79px; margin-right:1px\"><span style=\"font-size:13pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"color:black\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">&rarr;</span> <span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">Yes, S + was/were </span></span></span></span></p>\r\n\r\n<p style=\"margin-left:79px\"><span style=\"font-size:13pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"color:black\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">&rarr;</span> <span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">No, S+ wasn&rsquo;t/ weren&rsquo;t </span></span></span></span></p>\r\n\r\n<p style=\"margin-left:7px\"><span style=\"font-size:13pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"color:black\">&nbsp;</span></span></span></p>\r\n\r\n<p style=\"margin-left:31px\"><span style=\"font-size:13pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"color:black\"><span style=\"font-size:24.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">2.C&Aacute;CH D&Ugrave;NG </span></span></span></span></span></p>\r\n\r\n<ul>\r\n	<li style=\"list-style-type:none\">\r\n	<ul>\r\n		<li><span style=\"font-size:13pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"color:black\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">H&agrave;nh động đ&atilde; xảy ra v&agrave; k&eacute;o d&agrave;i 1 thời gian ở qu&aacute; khứ: </span></span></span></span></li>\r\n	</ul>\r\n	</li>\r\n</ul>\r\n\r\n<p style=\"margin-left:85px; margin-right:1px\"><span style=\"font-size:13pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"color:black\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">Yesterday, he was working in the lab all the afternoon. </span></span></span></span></p>\r\n\r\n<p style=\"margin-left:85px; margin-right:1px\"><span style=\"font-size:13pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"color:black\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">What were you doing from 3.m to 6m. yedterday? </span></span></span></span></p>\r\n\r\n<ul>\r\n	<li style=\"list-style-type:none\">\r\n	<ul>\r\n		<li><span style=\"font-size:13pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"color:black\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">H&agrave;nh động đang xảy ra v&agrave;o 1 thời t điểm cụ thể trong qu&aacute; khứ: </span></span></span></span></li>\r\n	</ul>\r\n	</li>\r\n</ul>\r\n\r\n<p style=\"margin-left:85px; margin-right:1px\"><span style=\"font-size:13pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"color:black\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">I was doing my homework at 8.m last night. </span></span></span></span></p>\r\n\r\n<ul>\r\n	<li style=\"list-style-type:none\">\r\n	<ul>\r\n		<li><span style=\"font-size:13pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"color:black\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">H&agrave;nh động xảy ra ở qu&aacute; khứ th&igrave; c&oacute; 1 h&agrave;nh động kh&aacute;c xen v&agrave;o: </span></span></span></span></li>\r\n	</ul>\r\n	</li>\r\n</ul>\r\n\r\n<p style=\"margin-left:91px; margin-right:1px\"><span style=\"font-size:13pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"color:black\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">When I came back home, he was cooking in the kitchen. </span></span></span></span></p>\r\n\r\n<ul>\r\n	<li style=\"list-style-type:none\">\r\n	<ul>\r\n		<li><span style=\"font-size:13pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"color:black\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">Hai h&agrave;nh động xảy ra song song c&ugrave;ng 1 l&uacute;c trong qu&aacute; khứ </span></span></span></span></li>\r\n	</ul>\r\n	</li>\r\n</ul>\r\n\r\n<p style=\"margin-left:91px; margin-right:1px\"><span style=\"font-size:13pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"color:black\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">While my father was reading book, my mother was cooking for dinner. </span></span></span></span></p>\r\n\r\n<p style=\"margin-left:7px\"><span style=\"font-size:13pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"color:black\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">Lưu &yacute;: Kh&ocirc;ng d&ugrave;ng QKTD cho c&aacute;c động từ chỉ tri gi&aacute;c, nhận thức. Thay v&agrave;o đ&oacute;, ta d&ugrave;ng QKĐ </span></span></span></span></p>\r\n\r\n<p style=\"margin-left:91px; margin-right:1px\"><span style=\"font-size:13pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"color:black\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">When the students heard the bell, they left. </span></span></span></span></p>\r\n\r\n<p style=\"margin-left:91px; margin-right:1px\"><span style=\"font-size:13pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"color:black\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">He felt tired at that time. </span></span></span></span></p>\r\n\r\n<p style=\"margin-left:7px\"><span style=\"font-size:13pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"color:black\">&nbsp;</span></span></span></p>\r\n\r\n<p style=\"margin-left:31px\"><span style=\"font-size:13pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"color:black\"><span style=\"font-size:24.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">3.DẤU HIỆU NHẬN BIẾT </span></span></span></span></span></p>\r\n\r\n<ul>\r\n	<li style=\"list-style-type:none\">\r\n	<ul>\r\n		<li><span style=\"font-size:13pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"color:black\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">At +&nbsp; giờ + thời gian trong qu&aacute; khứ : at 10 a.m last night. </span></span></span></span></li>\r\n		<li><span style=\"font-size:13pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"color:black\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">At this time + thời gian trong qu&aacute; khứ: At this time two weeks ago. </span></span></span></span></li>\r\n		<li><span style=\"font-size:13pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"color:black\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">In the past : trong qu&aacute; khứ </span></span></span></span></li>\r\n		<li><span style=\"font-size:13pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"color:black\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">Trong c&acirc;u c&oacute; when diễn tả 1 h&agrave;nh động th&igrave; 1 h&agrave;nh động kh&aacute;c xen v&agrave;o. (when, while, at this time : n&ecirc;n c&acirc;n nhắc khi xuất hiện trong c&acirc;u)</span> </span></span></span></li>\r\n	</ul>\r\n	</li>\r\n</ul>\r\n', 7, 0, '2018-12-21 10:35:46', '2018-12-21 10:35:46', 5, '', NULL, NULL),
(95, 'SIMPLE FUTURE (TƯƠNG LAI ĐƠN) ', '<p style=\"margin-left:31px\"><span style=\"font-size:13pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"color:black\"><span style=\"font-size:24.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">1.CẤU TR&Uacute;C </span></span></span></span></span></p>\r\n\r\n<ul>\r\n	<li style=\"list-style-type:none\">\r\n	<ul>\r\n		<li><span style=\"font-size:13pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"color:black\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">You/ He/She/ I/They/ We +&nbsp; Will + V &nbsp;&nbsp;&nbsp; (+) </span></span></span></span></li>\r\n		<li><span style=\"font-size:13pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"color:black\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">I/We + Will/Shall + V </span></span></span></span></li>\r\n		<li><span style=\"font-size:13pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"color:black\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">S+ won&rsquo;t/ shan&rsquo;t( shall not) + V &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; (-) </span></span></span></span></li>\r\n		<li><span style=\"font-size:13pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"color:black\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">Will/ Shall + S + V?&nbsp; &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; (?) </span></span></span></span></li>\r\n	</ul>\r\n	</li>\r\n</ul>\r\n\r\n<p style=\"margin-left:55px; margin-right:1px\"><span style=\"font-size:13pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"color:black\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">&rarr;</span> <span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">Yes, S + will/ shall </span></span></span></span></p>\r\n\r\n<p style=\"margin-left:55px\"><span style=\"font-size:13pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"color:black\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">&rarr;</span> <span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">No, S + won&rsquo;t shan&rsquo;t </span></span></span></span></p>\r\n\r\n<p style=\"margin-left:79px\"><span style=\"font-size:13pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"color:black\">&nbsp;</span></span></span></p>\r\n\r\n<p style=\"margin-left:31px\"><span style=\"font-size:13pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"color:black\"><span style=\"font-size:24.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">2.C&Aacute;CH D&Ugrave;NG </span></span></span></span></span></p>\r\n\r\n<ul>\r\n	<li style=\"list-style-type:none\">\r\n	<ul>\r\n		<li><span style=\"font-size:13pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"color:black\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">Diễn tả 1 h&agrave;nh động sẽ xảy ra ở tương lai </span></span></span></span></li>\r\n	</ul>\r\n	</li>\r\n</ul>\r\n\r\n<p style=\"margin-left:49px; margin-right:1px\"><span style=\"font-size:13pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"color:black\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">He will come back next month. </span></span></span></span></p>\r\n\r\n<ol>\r\n	<li style=\"list-style-type:none\">\r\n	<ol>\r\n		<li style=\"list-style-type:none\">\r\n		<ol style=\"list-style-type:upper-roman\">\r\n			<li><span style=\"font-size:13pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"color:black\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">will buy a house next year. </span></span></span></span></li>\r\n		</ol>\r\n		</li>\r\n		<li><span style=\"font-size:13pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"color:black\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">Diễn đạt &yacute; kiến, đưa ra 1 lời hứa hoặc 1 quyết định tức th&igrave; </span></span></span></span>\r\n		<ol style=\"list-style-type:upper-roman\">\r\n			<li><span style=\"font-size:13pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"color:black\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">think Chelsea will win. </span></span></span></span></li>\r\n		</ol>\r\n		</li>\r\n	</ol>\r\n	</li>\r\n</ol>\r\n\r\n<p><span style=\"font-size:13pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"color:black\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; <span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">A: &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; There is little milk for lunch, Lisa. </span></span></span></span></p>\r\n\r\n<p><span style=\"font-size:13pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"color:black\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; <span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">B: &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Ok, I will buy it now. </span></span></span></span></p>\r\n\r\n<p style=\"margin-left:31px\"><span style=\"font-size:13pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"color:black\"><span style=\"font-size:24.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">3.DẤU HIỆU NHẬN BIẾT </span></span></span></span></span></p>\r\n\r\n<p style=\"margin-left:57px; margin-right:1px\"><span style=\"font-size:13pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"color:black\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">C&oacute; c&aacute;c từ đi k&egrave;m: </span></span></span></span></p>\r\n\r\n<p style=\"margin-left:81px; margin-right:213px\"><span style=\"font-size:13pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"color:black\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">o</span> <span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">Someday: 1 ng&agrave;y n&agrave;o đ&oacute; </span><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">o</span> <span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">Tomorrow: ng&agrave;y mai </span><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">o</span> <span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">Next week/ month/ year,&hellip;: Tuần tới, th&aacute;ng tới, năm tới,&hellip; </span></span></span></span></p>\r\n', 7, 0, '2018-12-21 10:37:36', '2018-12-21 10:37:36', 5, '', NULL, NULL),
(90, 'SIMPLE PRESENT (HIỆN TẠI ĐƠN)', '<p style=\"margin-left:31px\"><span style=\"font-size:13pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"color:black\"><span style=\"font-size:24.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">1.CẤU TR&Uacute;C </span></span></span></span></span></p>\r\n\r\n<ul>\r\n	<li style=\"list-style-type:none\">\r\n	<ul>\r\n		<li><span style=\"font-size:13pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"color:black\"><span style=\"font-size:14.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">S + Vs/es&nbsp; &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; (+) &nbsp;&nbsp;&nbsp; </span></span><span style=\"font-size:14.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">&bull;</span></span> <span style=\"font-size:14.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">S + am/is/are + N/adj&nbsp;&nbsp; &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; (+) </span></span></span></span></span></li>\r\n		<li><span style=\"font-size:13pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"color:black\"><span style=\"font-size:14.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">S + don&rsquo;t/doesn&rsquo;t + V&nbsp; &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; (-) &nbsp;&nbsp;&nbsp;&nbsp; </span></span><span style=\"font-size:14.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">&bull;</span></span> <span style=\"font-size:14.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">S + am/is/are + not +&nbsp; N/adj&nbsp;&nbsp; &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; (-) </span></span></span></span></span></li>\r\n		<li><span style=\"font-size:13pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"color:black\"><span style=\"font-size:14.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">Do/Does + S + V?&nbsp;&nbsp; &nbsp;&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; (?) &nbsp;&nbsp;&nbsp; </span></span><span style=\"font-size:14.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">&bull;</span></span> <span style=\"font-size:14.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">Am/is/are + S + N/adj?&nbsp; &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; (?) </span></span></span></span></span></li>\r\n		<li><span style=\"font-size:13pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"color:black\"><span style=\"font-size:14.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">Yes, S + do/does.&nbsp; &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; </span></span><span style=\"font-size:14.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">&bull;</span></span> <span style=\"font-size:14.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">Yes, S + am/is/are </span></span></span></span></span></li>\r\n		<li><span style=\"font-size:13pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"color:black\"><span style=\"font-size:14.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">No, S + don&rsquo;t/doesn&rsquo;t. </span></span><span style=\"font-size:14.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">&bull;</span></span> <span style=\"font-size:14.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">No, S + am/is/ar</span></span></span></span></span></li>\r\n	</ul>\r\n	</li>\r\n</ul>\r\n\r\n<p style=\"margin-left:7px\"><span style=\"font-size:13pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"color:black\">&nbsp;</span></span></span></p>\r\n\r\n<p style=\"margin-left:31px\"><span style=\"font-size:13pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"color:black\"><span style=\"font-size:24.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">2.C&Aacute;CH D&Ugrave;NG </span></span></span></span></span></p>\r\n\r\n<ul>\r\n	<li style=\"list-style-type:none\">\r\n	<ul>\r\n		<li><span style=\"font-size:13pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"color:black\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">Ch&uacute;ng ta d&ugrave;ng th&igrave; hiện tại đơn để diễn tả một ch&acirc;n l&yacute;, một sự thật hiển nhi&ecirc;n xảy ra thường xuy&ecirc;n ở hiện tại. </span></span></span></span></li>\r\n	</ul>\r\n	</li>\r\n</ul>\r\n\r\n<p style=\"margin-left:79px; margin-right:1px\"><span style=\"font-size:13pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"color:black\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">The sun <u>rises</u> in the East.<em> </em></span></span></span></span></p>\r\n\r\n<p style=\"margin-left:78px\"><span style=\"font-size:13pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"color:black\"><em><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">&rarr;</span></em><em> </em><em><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">&nbsp;Mặt trời mọc ở đằng Đ&ocirc;ng.</span></em> </span></span></span></p>\r\n\r\n<p style=\"margin-left:79px; margin-right:1px\"><span style=\"font-size:13pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"color:black\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">They <u>live</u> in England.<em> </em></span></span></span></span></p>\r\n\r\n<p style=\"margin-left:79px; margin-right:157px\"><span style=\"font-size:13pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"color:black\"><em><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">&rarr;</span></em><em> </em><em><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">Họ sống ở nước Anh.</span></em> </span></span></span></p>\r\n\r\n<p style=\"margin-left:79px; margin-right:1px\"><span style=\"font-size:13pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"color:black\"><u><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">I&rsquo;m</span></u><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\"> a student.<em> </em></span></span></span></span></p>\r\n\r\n<p style=\"margin-left:79px; margin-right:157px\"><span style=\"font-size:13pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"color:black\"><em><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">&rarr;</span></em><em> </em><em>&nbsp;</em><em><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">T&ocirc;i l&agrave; một học sinh. </span></em></span></span></span></p>\r\n\r\n<p style=\"margin-left:43px\"><span style=\"font-size:13pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"color:black\"><em>&nbsp;</em></span></span></span></p>\r\n\r\n<ul>\r\n	<li style=\"list-style-type:none\">\r\n	<ul>\r\n		<li><span style=\"font-size:13pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"color:black\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">Ch&uacute;ng ta d&ugrave;ng th&igrave; hiện tại đơn để diễn tả một th&oacute;i quen, h&agrave;nh động thường xuy&ecirc;n xảy ra ở thời điểm hiện tại. </span></span></span></span></li>\r\n	</ul>\r\n	</li>\r\n</ul>\r\n\r\n<p style=\"margin-left:85px; margin-right:1px\"><span style=\"font-size:13pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"color:black\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">Mary often <u>goes</u> to school by bicycle.<em> </em></span></span></span></span></p>\r\n\r\n<p style=\"margin-left:78px\"><span style=\"font-size:13pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"color:black\"><em><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">&rarr;</span></em><em> </em><em><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">&nbsp;Mary thường đến trường bằng xe đạp.</span></em> </span></span></span></p>\r\n\r\n<p style=\"margin-left:85px; margin-right:1px\"><span style=\"font-size:13pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"color:black\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">I <u>get up</u> early every morning.<em> </em></span></span></span></span></p>\r\n\r\n<p style=\"margin-left:79px; margin-right:157px\"><span style=\"font-size:13pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"color:black\"><em><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">&rarr;</span></em><em> </em><em><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">&nbsp;T&ocirc;i dậy sớm mỗi s&aacute;ng.</span></em> </span></span></span></p>\r\n\r\n<p style=\"margin-left:85px; margin-right:1px\"><span style=\"font-size:13pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"color:black\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">What <u>do</u> you <u>do</u> every night?<em> </em></span></span></span></span></p>\r\n\r\n<p style=\"margin-left:79px; margin-right:157px\"><span style=\"font-size:13pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"color:black\"><em><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">&rarr;</span></em><em> </em><em><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">&nbsp;Bạn l&agrave;m g&igrave; mỗi tối? </span></em></span></span></span></p>\r\n\r\n<p style=\"margin-left:7px\"><span style=\"font-size:13pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"color:black\"><em>&nbsp;</em></span></span></span></p>\r\n\r\n<ul>\r\n	<li style=\"list-style-type:none\">\r\n	<ul>\r\n		<li><span style=\"font-size:13pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"color:black\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">Th&igrave; hiện tại đơn c&ograve;n được d&ugrave;ng để diễn tả h&agrave;nh động, sự việc tương lai sẽ xảy ra theo thời gian biểu hoặc chương tr&igrave;nh kế hoạch đ&atilde; định sẵn.&nbsp; </span></span></span></span></li>\r\n	</ul>\r\n	</li>\r\n</ul>\r\n\r\n<p style=\"margin-left:85px; margin-right:1px\"><span style=\"font-size:13pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"color:black\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">The train <u>leaves</u> at seven twenty-three tomorrow morning.<em> </em></span></span></span></span></p>\r\n\r\n<p style=\"margin-left:79px; margin-right:157px\"><span style=\"font-size:13pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"color:black\"><em><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">&rarr;</span></em><em> </em><em><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">&nbsp;Chuyến t&agrave;u khởi h&agrave;nh l&uacute;c 7:23 s&aacute;ng mai.</span></em> </span></span></span></p>\r\n\r\n<p style=\"margin-left:85px\"><span style=\"font-size:13pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"color:black\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">I&rsquo;ve got tour details here. We <u>spend</u> 3 days in Rome.<em> </em></span></span></span></span></p>\r\n\r\n<p style=\"margin-left:78px\"><span style=\"font-size:13pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"color:black\"><em><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">&rarr;</span></em><em> </em><em><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">&nbsp;T&ocirc;i đ&atilde; c&oacute; th&ocirc;ng tin về chuyến du lịch ở đ&acirc;y.Ch&uacute;ng ta sẽ d&agrave;nh 3 ng&agrave;y ở Rome.</span></em> </span></span></span></p>\r\n\r\n<p style=\"margin-left:7px\"><span style=\"font-size:13pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"color:black\">&nbsp;</span></span></span></p>\r\n\r\n<p><span style=\"font-size:13pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"color:black\"><span style=\"font-size:24.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">3.DẤU HIỆU NHẬN BIẾT </span></span></span></span></span></p>\r\n\r\n<p style=\"margin-left:7px; margin-right:1px\"><span style=\"font-size:13pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"color:black\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">Trong c&acirc;u c&oacute; c&aacute;c ph&oacute; từ sau: </span></span></span></span></p>\r\n\r\n<ul>\r\n	<li style=\"list-style-type:none\">\r\n	<ul>\r\n		<li style=\"list-style-type:none\">\r\n		<ul>\r\n			<li><span style=\"font-size:13pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"color:black\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">often, usually, frequently: thường&nbsp; </span></span></span></span></li>\r\n			<li><span style=\"font-size:13pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"color:black\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">always, constantly: lu&ocirc;n lu&ocirc;n </span></span></span></span></li>\r\n			<li><span style=\"font-size:13pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"color:black\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">sometimes, occasionally: thỉnh thoảng </span></span></span></span></li>\r\n			<li><span style=\"font-size:13pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"color:black\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">seldom, rarely: &iacute;t khi, hiếm khi </span></span></span></span></li>\r\n			<li><span style=\"font-size:13pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"color:black\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">every + day/week/month/year: h&agrave;ng ng&agrave;y/tuần/th&aacute;ng/năm </span></span></span></span></li>\r\n		</ul>\r\n		</li>\r\n	</ul>\r\n	</li>\r\n</ul>\r\n', 7, 0, '2018-12-21 10:31:45', '2018-12-21 10:31:45', 5, '', NULL, NULL);
INSERT INTO `posts` (`idtin`, `title`, `content`, `user_id`, `is_public`, `createdate`, `updatedate`, `idnhom`, `anhtrichdan`, `videotrichdan`, `noidungtrichdan`) VALUES
(91, 'PRESENT CONTINUOUS (HIỆN TẠI TIẾP DIỄN) ', '<p style=\"margin-left:31px\"><span style=\"font-size:13pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"color:black\"><span style=\"font-size:24.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">1.CẤU TR&Uacute;C </span></span></span></span></span></p>\r\n\r\n<ul>\r\n	<li style=\"list-style-type:none\">\r\n	<ul>\r\n		<li style=\"list-style-type:none\">\r\n		<ul>\r\n			<li><span style=\"font-size:13pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"color:black\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">S + am/is/are + V-ing.&nbsp; &nbsp;&nbsp;&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; (+) </span></span></span></span></li>\r\n			<li><span style=\"font-size:13pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"color:black\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">S + am/is/are + not + V-ing. &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; (-) </span><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">&bull;</span> <span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">Am/is/are + S + V-ing? &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; (?)</span><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\"></span></span></span></span></li>\r\n		</ul>\r\n		</li>\r\n	</ul>\r\n	</li>\r\n</ul>\r\n\r\n<p style=\"margin-left:79px; margin-right:1px\"><span style=\"font-size:13pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"color:black\"><em><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">&rarr;</span></em><em> </em><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">Yes, S + am/is/are. </span></span></span></span></p>\r\n\r\n<p style=\"margin-left:79px; margin-right:1px\"><span style=\"font-size:13pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"color:black\"><em><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">&rarr;</span></em><em> </em><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">No, S + am/is/are + not. </span></span></span></span></p>\r\n\r\n<p style=\"margin-left:7px\"><span style=\"font-size:13pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"color:black\">&nbsp;</span></span></span></p>\r\n\r\n<p style=\"margin-left:31px\"><span style=\"font-size:13pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"color:black\"><span style=\"font-size:24.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">2.C&Aacute;CH D&Ugrave;NG </span></span></span></span></span></p>\r\n\r\n<ul>\r\n	<li style=\"list-style-type:none\">\r\n	<ul>\r\n		<li><span style=\"font-size:13pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"color:black\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">Th&igrave; hiện tại tiếp diễn tả một h&agrave;nh động hay sự việc đang diễn ra trong l&uacute;c đang n&oacute;i. C&aacute;ch d&ugrave;ng n&agrave;y thường đi với trạng từ, trạng ngữ chỉ thời gian: now, right now, at the moment, at present,&hellip; </span></span></span></span></li>\r\n	</ul>\r\n	</li>\r\n</ul>\r\n\r\n<p style=\"margin-left:85px; margin-right:1px\"><span style=\"font-size:13pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"color:black\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">The children <u>are playing</u> football <u>now</u>.<em> </em></span></span></span></span></p>\r\n\r\n<p style=\"margin-left:78px\"><span style=\"font-size:13pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"color:black\"><em><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">&rarr;</span></em><em> </em><em><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">Lũ trẻ đang chơi b&oacute;ng đ&aacute;/b&oacute;ng bầu dục.</span></em> </span></span></span></p>\r\n\r\n<p style=\"margin-left:84px\"><span style=\"font-size:13pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"color:black\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">What <u>are you doing at the moment</u>?<em> </em></span></span></span></span></p>\r\n\r\n<p style=\"margin-left:78px\"><span style=\"font-size:13pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"color:black\"><em><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">&rarr;</span></em><em> </em><em><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">&nbsp;Bạn đang l&agrave;m g&igrave; vậy?</span></em> </span></span></span></p>\r\n\r\n<p style=\"margin-left:85px; margin-right:1px\"><span style=\"font-size:13pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"color:black\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">Be quiet! The baby <u>is sleeping</u>. <em>&nbsp;</em></span></span></span></span></p>\r\n\r\n<p style=\"margin-left:78px\"><span style=\"font-size:13pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"color:black\"><em><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">&rarr;</span></em><em> </em><em><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">&nbsp;Y&ecirc;n n&agrave;o! Em b&eacute; đang ngủ. </span></em></span></span></span></p>\r\n\r\n<p style=\"margin-left:7px\"><span style=\"font-size:13pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"color:black\"><em>&nbsp;</em></span></span></span></p>\r\n\r\n<ul>\r\n	<li style=\"list-style-type:none\">\r\n	<ul>\r\n		<li><span style=\"font-size:13pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"color:black\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">Th&igrave; hiện tại tiếp diễn diễn tả một h&agrave;nh động hay sự việc n&oacute;i chung đang diễn ra nhưng kh&ocirc;ng nhất thiết phải thực sự diễn ra ngay đ&uacute;ng thời điểm n&oacute;i. C&aacute;ch d&ugrave;ng n&agrave;y thường đi k&egrave;m: now, at the moment, this week, today, this term, this year,&hellip; </span></span></span></span></li>\r\n	</ul>\r\n	</li>\r\n</ul>\r\n\r\n<p style=\"margin-left:85px\"><span style=\"font-size:13pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"color:black\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">I&rsquo;m quite busy these days. <u>I&rsquo;m doing</u> a course at college.<em> </em></span></span></span></span></p>\r\n\r\n<p style=\"margin-left:78px\"><span style=\"font-size:13pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"color:black\"><em><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">&rarr;</span></em><em> </em><em><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">T&ocirc;i đang kh&aacute; bận mấy h&ocirc;m nay.T&ocirc;i đang thực hiện 1 kho&aacute; học ở trường.</span></em> </span></span></span></p>\r\n\r\n<p style=\"margin-left:85px; margin-right:1px\"><span style=\"font-size:13pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"color:black\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">The company I work for <u>is not doing</u> so well this year.<em> </em></span></span></span></span></p>\r\n\r\n<p style=\"margin-left:78px\"><span style=\"font-size:13pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"color:black\"><em><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">&rarr;</span></em><em> </em><em><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">C&ocirc;ng ty t&ocirc;i năm nay l&agrave;m việc kh&ocirc;ng được thuận lợi lắm. </span></em></span></span></span></p>\r\n\r\n<p style=\"margin-left:7px\"><span style=\"font-size:13pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"color:black\"><em>&nbsp;</em></span></span></span></p>\r\n\r\n<ul>\r\n	<li style=\"list-style-type:none\">\r\n	<ul>\r\n		<li><span style=\"font-size:13pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"color:black\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">Th&igrave; hiện tại tiếp diễn c&ograve;n được d&ugrave;ng để diễn tả một h&agrave;nh động sắp xảy ra (ở tương lai gần). </span></span></span></span></li>\r\n	</ul>\r\n	</li>\r\n</ul>\r\n\r\n<p style=\"margin-left:55px\"><span style=\"font-size:13pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"color:black\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">C&aacute;ch d&ugrave;ng n&agrave;y thường diễn tả một sự sắp xếp một kế hoạch đ&atilde; định. </span></span></span></span></p>\r\n\r\n<p style=\"margin-left:85px; margin-right:1px\"><span style=\"font-size:13pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"color:black\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">He <u>is coming</u> next week.<em> </em></span></span></span></span></p>\r\n\r\n<p style=\"margin-left:79px; margin-right:157px\"><span style=\"font-size:13pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"color:black\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">&rarr;</span> <em><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">Anh ấy sẽ đến v&agrave;o tuần tới.</span></em> </span></span></span></p>\r\n\r\n<p style=\"margin-left:85px; margin-right:1px\"><span style=\"font-size:13pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"color:black\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">My parents <u>are planting</u> trees tomorrow. <em>&nbsp;</em></span></span></span></span></p>\r\n\r\n<p style=\"margin-left:19px; margin-right:366px\"><span style=\"font-size:13pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"color:black\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">&rarr;</span> <em><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">Bố mẹ t&ocirc;i sẽ trồng c&acirc;y v&agrave;o ng&agrave;y mai</span></em> <strong>&nbsp;</strong></span></span></span></p>\r\n\r\n<p style=\"margin-left:31px\"><span style=\"font-size:13pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"color:black\"><span style=\"font-size:24.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">3.DẤU HIỆU NHẬN BIẾT </span></span></span></span></span></p>\r\n\r\n<ul>\r\n	<li style=\"list-style-type:none\">\r\n	<ul>\r\n		<li><span style=\"font-size:13pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"color:black\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">Trong c&acirc;u c&oacute; k&egrave;m theo:&nbsp; </span></span></span></span></li>\r\n	</ul>\r\n	</li>\r\n</ul>\r\n\r\n<p style=\"margin-left:79px; margin-right:239px\"><span style=\"font-size:13pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"color:black\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">o</span> <span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">Now, right now, at the moment, at present: b&acirc;y giờ </span><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">o</span> <span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">This week/month/term/year: tuần/th&aacute;ng/kỳ/năm n&agrave;y. </span></span></span></span></p>\r\n\r\n<ul>\r\n	<li style=\"list-style-type:none\">\r\n	<ul>\r\n		<li><span style=\"font-size:13pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"color:black\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">C&acirc;u đứng sau dấu chấm than. </span></span></span></span></li>\r\n		<li><span style=\"font-size:13pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"color:black\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">Kh&ocirc;ng d&ugrave;ng th&igrave; hiện tại tiếp diễn với c&aacute;c trường hợp c&oacute; động từ chỉ nhận thức, tri gi&aacute;c: hear, see, hear, understood, know, like, want, glance, feel, think, smell, love, hate, realize, seem, remember, forget,&hellip; </span></span></span></span></li>\r\n	</ul>\r\n	</li>\r\n</ul>\r\n', 7, 0, '2018-12-21 10:33:36', '2018-12-21 10:33:36', 5, '', NULL, NULL);
