INSERT INTO `posts` (`idtin`, `title`, `content`, `user_id`, `is_public`, `createdate`, `updatedate`, `idnhom`, `anhtrichdan`, `videotrichdan`, `noidungtrichdan`) VALUES
(106, 'Articles, Nouns and Pronouns (các mạo từ, danh từ và cụm Danh từ)  ', '<h2 style=\"margin-left:25px; margin-right:2px\"><span style=\"font-size:18pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"color:black\"><em><span style=\"font-size:14.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">a)</span></span></em><em> </em><em><span style=\"font-size:14.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">Indefinite article (Mạo từ bất định): A, an </span></span></em></span></span></span></h2>\r\n\r\n<ul>\r\n	<li><span style=\"font-size:13pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"color:black\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">&ldquo;A, an&rdquo; c&oacute; thể đứng trước danh từ đếm được số &iacute;t (singular countable nouns) v&agrave; được sử dụng trong lời n&oacute;i chung chung hoặc để giới thiệu một điều g&igrave; chưa được đề cập trước đ&oacute;. </span></span></span></span></li>\r\n	<li><span style=\"font-size:13pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"color:black\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">&ldquo;A&rdquo; được d&ugrave;ng đứng trước những từ bắt đầu bằng một phụ &acirc;m. </span></span></span></span></li>\r\n	<li><span style=\"font-size:13pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"color:black\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">&ldquo;An&rdquo; được d&ugrave;ng đứng trước những từ bắt đầu bằng một nguy&ecirc;n &acirc;m. </span></span></span></span></li>\r\n	<li><span style=\"font-size:13pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"color:black\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">i.e:&nbsp; </span></span></span></span>\r\n	<ul style=\"list-style-type:circle\">\r\n		<li><span style=\"font-size:13pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"color:black\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">A ball is round: n&oacute;i chung chung mọi tr&aacute;i b&oacute;ng đều c&oacute; h&igrave;nh tr&ograve;n. </span></span></span></span></li>\r\n		<li><span style=\"font-size:13pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"color:black\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">I saw a boy in the street: t&ocirc;i thấy một cậu b&eacute; tr&ecirc;n đường &ndash; kh&ocirc;ng r&otilde; cậu b&eacute; n&agrave;y l&agrave; ai nữa </span><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">o</span> <span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">An apple: một quả t&aacute;o&nbsp; </span><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">o</span> <span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">A pen: một chiếc b&uacute;t&nbsp; </span></span></span></span></li>\r\n	</ul>\r\n	</li>\r\n</ul>\r\n\r\n<p><span style=\"font-size:13pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"color:black\">&nbsp;</span></span></span></p>\r\n\r\n<p style=\"margin-left:25px\"><span style=\"font-size:13pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"color:black\"><em><span style=\"font-size:14.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">b)</span></span></em><em> </em><em><span style=\"font-size:14.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">Definite article (Mạo từ x&aacute;c định): The </span></span></em></span></span></span></p>\r\n\r\n<ul>\r\n	<li><span style=\"font-size:13pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"color:black\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">&ldquo;The&rdquo; được sử dụng để chỉ sự việc m&agrave; ch&uacute;ng ta đ&atilde; biết hoặc một kiến thức chung, phổ biến.&nbsp; </span></span></span></span></li>\r\n</ul>\r\n\r\n<p style=\"margin-left:73px; margin-right:1px\"><span style=\"font-size:13pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"color:black\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">o</span> <span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">i.e:&nbsp; </span></span></span></span></p>\r\n\r\n<ul>\r\n	<li style=\"list-style-type:none\">\r\n	<ul>\r\n		<li style=\"list-style-type:none\">\r\n		<ul>\r\n			<li style=\"list-style-type:none\">\r\n			<ul>\r\n				<li style=\"list-style-type:none\">\r\n				<ul>\r\n					<li><span style=\"font-size:13pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"color:black\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">The boy in the corner is my friend -&gt; &ldquo;boy&rdquo; trong c&acirc;u n&agrave;y cả người n&oacute;i v&agrave; người nghe đều biết l&agrave; ai. </span></span></span></span></li>\r\n					<li><span style=\"font-size:13pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"color:black\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">The Earth is round -&gt; chỉ c&oacute; duy nhất 1 Tr&aacute;i Đất (Earth) </span></span></span></span></li>\r\n				</ul>\r\n				</li>\r\n			</ul>\r\n			</li>\r\n		</ul>\r\n		</li>\r\n	</ul>\r\n	</li>\r\n	<li><span style=\"font-size:13pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"color:black\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">Với những danh từ kh&ocirc;ng đếm được, mạo từ &ldquo;the&rdquo; được d&ugrave;ng để n&oacute;i về c&aacute;i g&igrave; đ&oacute; ri&ecirc;ng biệt, nhưng sẽ kh&ocirc;ng d&ugrave;ng mạo từ để n&oacute;i về một c&aacute;i g&igrave; đấy chung chung. </span></span></span></span></li>\r\n</ul>\r\n\r\n<p style=\"margin-left:73px; margin-right:1px\"><span style=\"font-size:13pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"color:black\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">o</span> <span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">i.e:&nbsp; </span></span></span></span></p>\r\n\r\n<ul>\r\n	<li style=\"list-style-type:none\">\r\n	<ul>\r\n		<li style=\"list-style-type:none\">\r\n		<ul>\r\n			<li style=\"list-style-type:none\">\r\n			<ul>\r\n				<li><span style=\"font-size:13pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"color:black\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">Sugar is sweet: Đường th&igrave; ngọt &ndash; n&oacute;i chung chung, kh&ocirc;ng cụ thể loại đường n&agrave;o. </span></span></span></span></li>\r\n				<li><span style=\"font-size:13pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"color:black\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">The sugar in the table is from Cuba: Đường ở tr&ecirc;n b&agrave;n xuất xứ ở Cuba &ndash; lần n&agrave;y th&igrave; &ldquo;sugar&rdquo; được nhắc đến cụ thể rồi.&nbsp;&nbsp; </span></span></span></span></li>\r\n			</ul>\r\n			</li>\r\n		</ul>\r\n		</li>\r\n	</ul>\r\n	</li>\r\n	<li><span style=\"font-size:13pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"color:black\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">Th&ocirc;ng thường, với danh từ đếm được số nhiều khi n&oacute;i đến 1 loại n&agrave;o đ&oacute; th&igrave; kh&ocirc;ng c&oacute; &ldquo;the&rdquo; đi trước.&nbsp; </span></span></span></span>\r\n	<ul style=\"list-style-type:circle\">\r\n		<li><span style=\"font-size:13pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"color:black\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">i.e:&nbsp; </span></span></span></span>\r\n		<ul>\r\n			<li style=\"list-style-type:none\">\r\n			<ul>\r\n				<li><span style=\"font-size:13pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"color:black\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">Oranges are green until they ripen: những quả cam đều c&oacute; m&agrave;u xanh tới khi ch&uacute;ng hỏng. </span></span></span></span></li>\r\n				<li><span style=\"font-size:13pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"color:black\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">Athletes should follow a well-balanced diet: c&aacute;c vận động vi&ecirc;n n&ecirc;n theo chế độ ăn ki&ecirc;ng điều độ. </span></span></span></span></li>\r\n			</ul>\r\n			</li>\r\n		</ul>\r\n		</li>\r\n	</ul>\r\n	</li>\r\n	<li><span style=\"font-size:13pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"color:black\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">Với danh từ ri&ecirc;ng, sẽ c&oacute; mạo từ &ldquo;the&rdquo; đi trước nếu người n&oacute;i muốn x&aacute;c định r&otilde;. </span></span></span></span>\r\n	<ul style=\"list-style-type:circle\">\r\n		<li><span style=\"font-size:13pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"color:black\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">i.e:&nbsp; </span></span></span></span></li>\r\n	</ul>\r\n	</li>\r\n</ul>\r\n\r\n<p style=\"margin-left:144px; margin-right:1px\"><span style=\"font-size:13pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"color:black\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">▪</span> <span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">The Susan Parker that I know live on First Avenue: C&ocirc; Susan Parker m&agrave; t&ocirc;i biết sống ở đại lộ First. </span></span></span></span></p>\r\n\r\n<ul>\r\n	<li><span style=\"font-size:13pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"color:black\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">Những từ như: Breakfast, lunch, dinner, school, church, home, college, work,&hellip; kh&ocirc;ng sử dụng bất cứ từ n&agrave;o nếu kh&ocirc;ng c&oacute; giới hạn nghĩa.&nbsp; </span></span></span></span>\r\n\r\n	<ul style=\"list-style-type:circle\">\r\n		<li><span style=\"font-size:13pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"color:black\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">i.e:&nbsp; </span></span></span></span>\r\n\r\n		<ul>\r\n			<li style=\"list-style-type:none\">\r\n			<ul>\r\n				<li><span style=\"font-size:13pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"color:black\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">We ate breakfast at 8 o&rsquo;clock this morning: ch&uacute;ng t&ocirc;i đ&atilde; ăn s&aacute;ng v&agrave;o 8 giờ s&aacute;ng nay. </span></span></span></span></li>\r\n				<li><span style=\"font-size:13pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"color:black\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">We went to school yesterday: ch&uacute;ng t&ocirc;i đ&atilde; đi học ng&agrave;y h&ocirc;m qua. </span></span></span></span></li>\r\n			</ul>\r\n			</li>\r\n		</ul>\r\n		</li>\r\n	</ul>\r\n	</li>\r\n</ul>\r\n\r\n<p style=\"margin-left:25px\"><span style=\"font-size:13pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"color:black\"><em><span style=\"font-size:14.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">c)</span></span></em><em> </em><em><span style=\"font-size:14.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">Adjectives and Adverbs (C&aacute;c T&iacute;nh từ v&agrave; Trạng từ) </span></span></em></span></span></span></p>\r\n\r\n<p><span style=\"font-size:13pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"color:black\">&nbsp;&nbsp;&nbsp; <span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">i.</span> &nbsp;&nbsp;&nbsp;&nbsp; <span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">Ph&acirc;n loại t&iacute;nh từ: </span></span></span></span></p>\r\n\r\n<ul>\r\n	<li><span style=\"font-size:13pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"color:black\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">T&iacute;nh từ m&ocirc; tả: l&agrave; những t&iacute;nh từ d&ugrave;ng để m&ocirc; tả m&agrave;u sắc, k&iacute;ch thước, phẩm chất,&hellip; của người hoặc sự vật. </span></span></span></span></li>\r\n</ul>\r\n\r\n<p style=\"margin-left:73px; margin-right:1px\"><span style=\"font-size:13pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"color:black\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">o</span> <span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">i.e:&nbsp; </span></span></span></span></p>\r\n\r\n<ul>\r\n	<li style=\"list-style-type:none\">\r\n	<ul>\r\n		<li style=\"list-style-type:none\">\r\n		<ul>\r\n			<li><span style=\"font-size:13pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"color:black\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">Good: tốt </span></span></span></span></li>\r\n			<li><span style=\"font-size:13pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"color:black\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">Bright: s&aacute;ng ch&oacute;i </span></span></span></span></li>\r\n			<li><span style=\"font-size:13pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"color:black\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">Tall: cao </span></span></span></span></li>\r\n		</ul>\r\n		</li>\r\n	</ul>\r\n	</li>\r\n	<li><span style=\"font-size:13pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"color:black\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">T&iacute;nh từ giới hạn: l&agrave; những t&iacute;nh từ đặt giới hạn cho những từ m&agrave; n&oacute; bổ nghĩa </span></span></span></span></li>\r\n	<li><span style=\"font-size:13pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"color:black\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">T&iacute;nh từ sở hữu (lu&ocirc;n c&oacute; danh từ theo sau) </span><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">o</span> <span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">My: của t&ocirc;i&nbsp; </span><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">o</span> <span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">His: của anh ấy </span><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">o</span> <span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">Her: của c&ocirc; ấy </span><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">o</span> <span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">Its: của n&oacute; </span><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">o</span> <span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">Our: của ch&uacute;ng ta </span><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">o</span> <span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">Your: của bạn/của c&aacute;c bạn </span><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">o</span> <span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">Their: của họ </span><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">o</span> <span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">i.e:&nbsp; </span></span></span></span>\r\n	<ul>\r\n		<li style=\"list-style-type:none\">\r\n		<ul>\r\n			<li><span style=\"font-size:13pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"color:black\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">My wife: vợ của t&ocirc;i. </span></span></span></span></li>\r\n			<li><span style=\"font-size:13pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"color:black\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">His book: cuốn s&aacute;ch của anh ấy. </span></span></span></span></li>\r\n			<li><span style=\"font-size:13pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"color:black\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">T&iacute;nh từ chỉ định: this, that, those(dạng số nhiều của &ldquo;this), these (dạng số nhiều của &ldquo;that&rdquo;).&nbsp; </span></span></span></span></li>\r\n		</ul>\r\n		</li>\r\n	</ul>\r\n	</li>\r\n</ul>\r\n\r\n<p style=\"margin-left:73px; margin-right:1px\"><span style=\"font-size:13pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"color:black\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">o</span> <span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">&nbsp;i.e:&nbsp; </span></span></span></span></p>\r\n\r\n<ul>\r\n	<li style=\"list-style-type:none\">\r\n	<ul>\r\n		<li style=\"list-style-type:none\">\r\n		<ul>\r\n			<li style=\"list-style-type:none\">\r\n			<ul>\r\n				<li><span style=\"font-size:13pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"color:black\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">I don&rsquo;t like those people: t&ocirc;i kh&ocirc;ng th&iacute;ch những người đ&oacute;. </span></span></span></span></li>\r\n				<li><span style=\"font-size:13pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"color:black\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">I want this car: t&ocirc;i muốn chiếc xe n&agrave;y. </span><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">▪</span> <span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">Từ x&aacute;c định c&oacute; chức năng l&agrave;m t&iacute;nh từ: </span></span></span></span></li>\r\n			</ul>\r\n			</li>\r\n		</ul>\r\n		</li>\r\n	</ul>\r\n	</li>\r\n	<li><span style=\"font-size:13pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"color:black\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">Từ x&aacute;c định c&oacute; thể đứng trước danh từ số nhiều hoặc số &iacute;t, đếm được hoặc kh&ocirc;ng đếm được. Động từ theo sau c&oacute; thể ở dạng số nhiều hoặc số &iacute;t tuỳ loại: some (một v&agrave;i), a few (một &iacute;t), several (v&agrave;i), both (cả hai), each (mỗi), every (mọi), either (một trong hai), neither (kh&ocirc;ng g&igrave; trong cả hai), all (tất cả), much (nhiều), little (ch&uacute;t).&nbsp; </span></span></span></span></li>\r\n	<li><span style=\"font-size:13pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"color:black\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">&ldquo;Much&rdquo;, &ldquo;little&rdquo; d&ugrave;ng với danh từ kh&ocirc;ng đếm được: </span></span></span></span></li>\r\n</ul>\r\n\r\n<p style=\"margin-left:73px; margin-right:1px\"><span style=\"font-size:13pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"color:black\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">o</span> <span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">i.e:&nbsp; </span></span></span></span></p>\r\n\r\n<ul>\r\n	<li style=\"list-style-type:none\">\r\n	<ul>\r\n		<li style=\"list-style-type:none\">\r\n		<ul>\r\n			<li style=\"list-style-type:none\">\r\n			<ul>\r\n				<li><span style=\"font-size:13pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"color:black\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">Much time, little money.&nbsp; </span></span></span></span></li>\r\n				<li><span style=\"font-size:13pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"color:black\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">&rdquo;A lot of, plenty of, lots of&rdquo;:&nbsp; </span></span></span></span></li>\r\n				<li><span style=\"font-size:13pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"color:black\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">&ldquo;A lot of&rdquo;: rất nhiều &ndash; được d&ugrave;ng trong c&acirc;u khẳng định hơn l&agrave; much, many.&nbsp; </span></span></span></span></li>\r\n			</ul>\r\n			</li>\r\n		</ul>\r\n		</li>\r\n	</ul>\r\n	</li>\r\n</ul>\r\n\r\n<p><span style=\"font-size:13pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"color:black\">&nbsp;</span></span></span></p>\r\n\r\n<p><span style=\"font-size:13pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"color:black\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">ii.</span> &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; <span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">Ph&acirc;n từ d&ugrave;ng l&agrave;m t&iacute;nh từ </span></span></span></span></p>\r\n\r\n<ul>\r\n	<li><span style=\"font-size:13pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"color:black\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">C&oacute; một số t&iacute;nh từ xuất ph&aacute;t từ động từ + ing/ ed: interesting/ interested (th&uacute; vị, hay), boring/bored (ch&aacute;n), exciting/excited (h&aacute;o hức),&hellip; </span><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">o</span> <span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">i.e:&nbsp; </span></span></span></span>\r\n\r\n	<ul>\r\n		<li style=\"list-style-type:none\">\r\n		<ul>\r\n			<li style=\"list-style-type:none\">\r\n			<ul>\r\n				<li><span style=\"font-size:13pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"color:black\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">Sombody is bored if something is boring. </span></span></span></span></li>\r\n				<li><span style=\"font-size:13pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"color:black\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">If something is boring, it makes you bored. </span></span></span></span></li>\r\n			</ul>\r\n			</li>\r\n		</ul>\r\n		</li>\r\n	</ul>\r\n	</li>\r\n	<li><span style=\"font-size:13pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"color:black\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">T&iacute;nh từ tận c&ugrave;ng bằng &ldquo;-ing&rdquo; cho biết t&iacute;nh chất c&ocirc;ng việc, sự việc. </span></span></span></span>\r\n	<ul style=\"list-style-type:circle\">\r\n		<li><span style=\"font-size:13pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"color:black\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">i.e:&nbsp; </span></span></span></span>\r\n		<ul>\r\n			<li><span style=\"font-size:13pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"color:black\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">Julia thinks politics is very interesting. </span></span></span></span></li>\r\n			<li><span style=\"font-size:13pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"color:black\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">I was quite surprised that he passed the examination.&nbsp; </span></span></span></span></li>\r\n		</ul>\r\n		</li>\r\n	</ul>\r\n	</li>\r\n	<li><span style=\"font-size:13pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"color:black\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">T&iacute;nh từ tận c&ugrave;ng bằng &ldquo;-ed&rdquo; cho biết trạng th&aacute;i t&acirc;m trạng của một người. </span></span></span></span>\r\n	<ul style=\"list-style-type:circle\">\r\n		<li><span style=\"font-size:13pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"color:black\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">i.e:&nbsp; </span></span></span></span>\r\n		<ul>\r\n			<li><span style=\"font-size:13pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"color:black\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">Julia is very interested in politics. </span></span></span></span></li>\r\n			<li><span style=\"font-size:13pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"color:black\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">Everybody was surprised that he passed the examination. </span></span></span></span></li>\r\n		</ul>\r\n		</li>\r\n	</ul>\r\n	</li>\r\n	<li><span style=\"font-size:13pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"color:black\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">Danh từ d&ugrave;ng l&agrave;m t&iacute;nh từ </span></span></span></span></li>\r\n	<li><span style=\"font-size:13pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"color:black\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">Trong tiếng anh, danh từ c&oacute; thể được d&ugrave;ng như t&iacute;nh từ để bổ nghĩa cho danh từ kh&aacute;c. </span></span></span></span>\r\n	<ul style=\"list-style-type:circle\">\r\n		<li><span style=\"font-size:13pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"color:black\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">i.e:&nbsp; </span></span></span></span>\r\n		<ul>\r\n			<li><span style=\"font-size:13pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"color:black\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">A wool coat a gold watch, a history teacher. </span></span></span></span></li>\r\n		</ul>\r\n		</li>\r\n	</ul>\r\n	</li>\r\n	<li><span style=\"font-size:13pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"color:black\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">Danh từ đầu ti&ecirc;n của sự kết hợp c&oacute; chức năng như một t&iacute;nh từ. Những danh từ c&oacute; chức năng như t&iacute;nh từ lu&ocirc;n ở h&igrave;nh thức số &iacute;t ngay khi ch&uacute;ng bổ nghĩa cho danh từ số nhiều. Sự kết hợp giữa số + danh từ lu&ocirc;n c&oacute; dấu c&aacute;ch ngang. </span></span></span></span>\r\n	<ul style=\"list-style-type:circle\">\r\n		<li><span style=\"font-size:13pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"color:black\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">i.e:&nbsp; </span></span></span></span>\r\n		<ul>\r\n			<li><span style=\"font-size:13pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"color:black\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">We took a tour that lasted five weeks. (noun) </span></span></span></span></li>\r\n			<li><span style=\"font-size:13pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"color:black\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">We tooks a five-week tour. (adjective) </span></span></span></span></li>\r\n			<li><span style=\"font-size:13pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"color:black\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">That student wrote a report that was ten pages long. </span></span></span></span></li>\r\n			<li><span style=\"font-size:13pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"color:black\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">That student wrote a ten-page report. </span></span></span></span></li>\r\n		</ul>\r\n		</li>\r\n	</ul>\r\n	</li>\r\n</ul>\r\n\r\n<ol start=\"3\" style=\"list-style-type:lower-roman\">\r\n	<li><span style=\"font-size:13pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"color:black\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">Chức năng của t&iacute;nh từ </span></span></span></span>\r\n\r\n	<ul>\r\n		<li><span style=\"font-size:13pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"color:black\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">Bổ nghĩa cho danh từ (modify for a noun):&nbsp; </span></span></span></span>\r\n\r\n		<ul style=\"list-style-type:circle\">\r\n			<li><span style=\"font-size:13pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"color:black\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">Thường đứng ngay trước danh từ được n&oacute; bổ nghĩa. </span></span></span></span></li>\r\n			<li><span style=\"font-size:13pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"color:black\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">i.e:&nbsp; </span></span></span></span>\r\n			<ul>\r\n				<li><span style=\"font-size:13pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"color:black\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">He is a nice man. </span></span></span></span></li>\r\n				<li><span style=\"font-size:13pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"color:black\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">Do you see the small green boat, which has such an odd shape? </span></span></span></span></li>\r\n			</ul>\r\n			</li>\r\n			<li><span style=\"font-size:13pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"color:black\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">T&iacute;nh từ c&oacute; thể đứng sau danh từ được n&oacute; bổ nghĩa trong c&aacute;c trường hợp sau: </span><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">o</span> <span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">Khi t&iacute;nh từ được d&ugrave;ng với c&aacute;c từ &ldquo;something&rdquo;, &ldquo;someone&rdquo;, &ldquo;anything&rdquo;, &ldquo;everything&rdquo;, &hellip; . </span></span></span></span></li>\r\n			<li><span style=\"font-size:13pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"color:black\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">i.e:&nbsp; </span></span></span></span>\r\n			<ul>\r\n				<li><span style=\"font-size:13pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"color:black\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">I&rsquo;ll tell you something interesting: t&ocirc;i sẽ kể cho bạn v&agrave;i thứ th&uacute; vị.&nbsp; </span></span></span></span></li>\r\n				<li><span style=\"font-size:13pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"color:black\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">Is there anything new?: c&oacute; điều g&igrave; mới kh&ocirc;ng? </span></span></span></span></li>\r\n			</ul>\r\n			</li>\r\n			<li><span style=\"font-size:13pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"color:black\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">Khi t&iacute;nh từ được t&aacute;ch rời trong c&acirc;u. </span></span></span></span></li>\r\n			<li><span style=\"font-size:13pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"color:black\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">i.e:&nbsp; </span></span></span></span>\r\n			<ul>\r\n				<li><span style=\"font-size:13pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"color:black\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">My father, happy and tired, kiss me a good night: bố t&ocirc;i, &ocirc;ng hạnh ph&uacute;c v&agrave; mệt mỏi, h&ocirc;n t&ocirc;i ch&uacute;c ngủ ngon. </span></span></span></span></li>\r\n				<li><span style=\"font-size:13pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"color:black\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">Bổ ngữ của động từ (complement of a verb): đi sau c&aacute;c động từ li&ecirc;n kết (linking verb: be, get, look, &hellip; ). </span></span></span></span></li>\r\n			</ul>\r\n			</li>\r\n			<li><span style=\"font-size:13pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"color:black\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">i.e:&nbsp; </span></span></span></span>\r\n			<ul>\r\n				<li><span style=\"font-size:13pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"color:black\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">The weather becomes cold: thời tiết trở lạnh. </span></span></span></span></li>\r\n				<li><span style=\"font-size:13pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"color:black\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">He look mature, sober and calm: anh ấy tr&ocirc;ng c&oacute; vẻ trưởng th&agrave;nh, điềm đạm v&agrave; b&igrave;nh tĩnh.&nbsp; </span></span></span></span></li>\r\n			</ul>\r\n			</li>\r\n		</ul>\r\n		</li>\r\n		<li><span style=\"font-size:13pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"color:black\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">Bổ ngữ của t&acirc;n ngữ (objective complement).&nbsp; </span></span></span></span>\r\n		<ul style=\"list-style-type:circle\">\r\n			<li><span style=\"font-size:13pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"color:black\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">i.e:&nbsp; </span></span></span></span>\r\n			<ul>\r\n				<li><span style=\"font-size:13pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"color:black\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">She wears her hair short: c&ocirc; ấy c&oacute; m&aacute;i t&oacute;c ngắn.&nbsp;&nbsp; </span></span></span></span></li>\r\n				<li><span style=\"font-size:13pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"color:black\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">We should keep our room clean and tidy: ch&uacute;ng ta n&ecirc;n giữ ph&ograve;ng m&igrave;nh sạch sẽ v&agrave; gọn g&agrave;ng. </span></span></span></span></li>\r\n			</ul>\r\n			</li>\r\n		</ul>\r\n		</li>\r\n		<li><span style=\"font-size:13pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"color:black\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">Bổ ngữ của chủ ngữ (subjective complement). </span></span></span></span>\r\n		<ul style=\"list-style-type:circle\">\r\n			<li><span style=\"font-size:13pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"color:black\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">i.e:&nbsp; </span></span></span></span>\r\n			<ul>\r\n				<li><span style=\"font-size:13pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"color:black\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">Her hair was dyed blonde: t&oacute;c c&ocirc; ấy đ&atilde; được nhuộm v&agrave;ng. </span></span></span></span></li>\r\n			</ul>\r\n			</li>\r\n		</ul>\r\n		</li>\r\n	</ul>\r\n	</li>\r\n</ol>\r\n\r\n<p><span style=\"font-size:13pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"color:black\">&nbsp;</span></span></span></p>\r\n\r\n<ol start=\"4\" style=\"list-style-type:lower-roman\">\r\n	<li><span style=\"font-size:13pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"color:black\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">Trật tự của t&iacute;nh từ </span></span></span></span>\r\n\r\n	<ul>\r\n		<li><span style=\"font-size:13pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"color:black\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">Đ&ocirc;i khi ch&uacute;ng ta d&ugrave;ng hai hay nhiều t&iacute;nh từ đi với nhau. </span></span></span></span>\r\n\r\n		<ul style=\"list-style-type:circle\">\r\n			<li><span style=\"font-size:13pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"color:black\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">i.e:&nbsp; </span></span></span></span>\r\n\r\n			<ul>\r\n				<li><span style=\"font-size:13pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"color:black\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">My brother lives in a nice, new house: anh t&ocirc;i sống trong một ng&ocirc;i nh&agrave; đẹp v&agrave; mới.&nbsp; </span></span></span></span></li>\r\n				<li><span style=\"font-size:13pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"color:black\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">In the kitchen, there is a beautiful large round wooden table: ở trong bếp c&oacute; một chiếc b&agrave;n gỗ đẹp, tr&ograve;n v&agrave; rộng.&nbsp; </span></span></span></span></li>\r\n			</ul>\r\n			</li>\r\n		</ul>\r\n		</li>\r\n		<li><span style=\"font-size:13pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"color:black\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">Những t&iacute;nh từ như &ldquo;new&rdquo;, &ldquo;large&rdquo;, &ldquo;round&rdquo;, &ldquo;wooden&rdquo; l&agrave; những t&iacute;nh từ mi&ecirc;u tả. Những t&iacute;nh từ như &ldquo;nice&rdquo;, &ldquo;beautiful&rdquo; l&agrave; những t&iacute;nh từ chỉ cảm nghĩ. </span></span></span></span></li>\r\n		<li><span style=\"font-size:13pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"color:black\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">T&iacute;nh từ chỉ cảm nghĩ thường d&ugrave;ng trước t&iacute;nh từ mi&ecirc;u tả. </span></span></span></span>\r\n		<ul style=\"list-style-type:circle\">\r\n			<li><span style=\"font-size:13pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"color:black\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">i.e:&nbsp; </span></span></span></span>\r\n			<ul>\r\n				<li><span style=\"font-size:13pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"color:black\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">A nice long summer holiday: một kỳ nghỉ h&egrave; d&agrave;i tuyệt vời.&nbsp; </span></span></span></span></li>\r\n				<li><span style=\"font-size:13pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"color:black\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">An interesting young man: m&ocirc;t người thanh ni&ecirc;n th&uacute; vị. </span></span></span></span></li>\r\n			</ul>\r\n			</li>\r\n		</ul>\r\n		</li>\r\n		<li><span style=\"font-size:13pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"color:black\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">Khi hai hay nhiều t&iacute;nh từ mi&ecirc;u tả được d&ugrave;ng trong một c&acirc;u. Trật tự của ch&uacute;ng được sắp xếp như sau: </span></span></span></span>\r\n		<ul style=\"list-style-type:circle\">\r\n			<li><span style=\"font-size:13pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"color:black\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">Opinion - t&iacute;nh từ chỉ quan điểm, sự đ&aacute;nh gi&aacute;. (beautiful, wonderful, terrible&hellip;) </span><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">o</span> <span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">Size - t&iacute;nh từ chỉ k&iacute;ch cỡ. (big, small, long, short, tall&hellip;) </span><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">o</span> <span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">Age - t&iacute;nh từ chỉ độ tuổi. (old, young, old, new&hellip;) </span><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">o</span> <span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">Shape - t&iacute;nh từ chỉ h&igrave;nh dạng (circular, square, round, rectangle...) </span><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">o</span> <span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">Color - t&iacute;nh từ chỉ m&agrave;u sắc. (orange, yellow, light blue&hellip;) </span><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">o</span> <span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">Origin &ndash; t&iacute;nh từ chỉ nguồn gốc, xuất xứ. (Japanese, American, Vietnamese&hellip;) </span><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">o</span> <span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">Material &ndash; t&iacute;nh từ chỉ chất liệu. (stone, plastic, leather, steel, silk&hellip;) </span><span style=\"font-size:11.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">o</span></span> <span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">Purpose &ndash; t&iacute;nh từ chỉ mục đ&iacute;ch, t&aacute;c dụng.</span> </span></span></span></li>\r\n		</ul>\r\n		</li>\r\n	</ul>\r\n	</li>\r\n</ol>\r\n', 7, 0, '2018-12-24 15:41:56', '2018-12-24 15:41:56', 7, '', NULL, NULL),
(107, 'Comparision of Adverb and Adjective (phép so sánh về Tính từ và Trạng từ)  ', '<p style=\"margin-left:25px\"><span style=\"font-size:13pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"color:black\"><em><span style=\"font-size:14.0pt\">a)</span></em><em> </em><em><span style=\"font-size:14.0pt\">So s&aacute;nh bằng &ndash; Equal comparisions: </span></em></span></span></span></p>\r\n\r\n<ul>\r\n	<li><span style=\"font-size:13pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"color:black\">S + V.inf + as + adverb + as + noun/pronoun/phrase. </span></span></span></li>\r\n	<li><span style=\"font-size:13pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"color:black\">S + to be + as adjective + as + noun/pronoun/phrase. </span></span></span>\r\n	<ul style=\"list-style-type:circle\">\r\n		<li><span style=\"font-size:13pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"color:black\">i.e:&nbsp; </span></span></span>\r\n		<ul>\r\n			<li><span style=\"font-size:13pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"color:black\">He is as tall as his father: Anh ấy cao bằng bố của anh ấy ▪ She worked as well as you did: C&ocirc; ấy đ&atilde; l&agrave;m việc tốt như bạn. </span></span></span></li>\r\n		</ul>\r\n		</li>\r\n	</ul>\r\n	</li>\r\n	<li><span style=\"font-size:13pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"color:black\">&ldquo;So&rdquo; c&oacute; thể d&ugrave;ng thay &ldquo;as&rdquo; trong c&acirc;u phủ định. </span></span></span>\r\n	<ul style=\"list-style-type:circle\">\r\n		<li><span style=\"font-size:13pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"color:black\">i.e: </span></span></span></li>\r\n	</ul>\r\n	</li>\r\n</ul>\r\n\r\n<p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;&nbsp;</p>\r\n\r\n<ul>\r\n	<li style=\"list-style-type:none\">\r\n	<ul>\r\n		<li style=\"list-style-type:none\">\r\n		<ul>\r\n			<li><span style=\"font-size:13pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"color:black\">Peter does not drive so carefully as me. -&gt; Peter kh&ocirc;ng l&aacute;i xe cẩn thận như t&ocirc;i.&nbsp; </span></span></span></li>\r\n		</ul>\r\n		</li>\r\n	</ul>\r\n	</li>\r\n	<li><span style=\"font-size:13pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"color:black\">&Yacute; &ldquo;bằng nhau, như nhau&rdquo; c&oacute; thể được diễn tả bằng:&nbsp; </span></span></span>\r\n	<ul style=\"list-style-type:circle\">\r\n		<li><span style=\"font-size:13pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"color:black\">S + V + the same + (noun) + as + noun/pronoun. </span></span></span></li>\r\n	</ul>\r\n	</li>\r\n</ul>\r\n\r\n<p><span style=\"font-size:13pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"color:black\">&nbsp;</span></span></span></p>\r\n\r\n<p style=\"margin-left:25px\"><span style=\"font-size:13pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"color:black\"><em><span style=\"font-size:14.0pt\">b)</span></em><em> </em><em><span style=\"font-size:14.0pt\">So s&aacute;nh kh&ocirc;ng bằng &ndash; Unequal comparisions: </span></em></span></span></span></p>\r\n\r\n<p>&nbsp;&nbsp;&nbsp; <span style=\"font-size:13.0pt\"><span style=\"color:black\">i.</span></span> &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; <span style=\"font-size:13.0pt\"><span style=\"color:black\">So s&aacute;nh bậc nhất &ndash; Suprelatives:&nbsp; </span></span></p>\r\n\r\n<p>&quot; &gt;</p>\r\n', 7, 0, '2018-12-24 15:44:06', '2018-12-24 15:44:06', 7, '', NULL, NULL);
INSERT INTO `posts` (`idtin`, `title`, `content`, `user_id`, `is_public`, `createdate`, `updatedate`, `idnhom`, `anhtrichdan`, `videotrichdan`, `noidungtrichdan`) VALUES
(109, 'I. PASSAGE 1 ', '<p style=\"margin-left:7px; margin-right:1px\"><span style=\"font-size:13pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"color:black\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">In today&#39;s competitive world, what responsible parent would not want to give their children the best possible start in life? For this reason, many parents want their children, often as young as ten months old, to become familiar with computers. They seem to think that if their children grow up with computers, they will be better equipped to face the challenges of the future.&nbsp; </span></span></span></span></p>\r\n\r\n<p style=\"margin-left:7px; margin-right:1px\"><span style=\"font-size:13pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"color:black\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">No one has proved that computers make children more creative or more intelligent. The truth may even be the opposite. Educational psychologists claim that too much exposure to computers, especially for the very young, may negatively affect normal brain development. Children gain valuable experience of the world from their interaction with physical objects. Ten- month-old babies may benefit more from bumping their heads or putting various objects in their mouths than they will from staring at eye-catching cartoons. A four-year-old child can improve hand-eye coordination and understand cause and effect better by experimenting with a crayon than by moving a cursor around a computer screen. So, as educational psychologists suggest, instead of government funding going to more and more computer classes, it might be better to devote resources to music and art programs.&nbsp; </span></span></span></span></p>\r\n\r\n<p style=\"margin-left:7px; margin-right:1px\"><span style=\"font-size:13pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"color:black\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">It is <u>ludicrous</u> to think that children will fall behind if they are not i.eposed to computers from an early age. Time is too precious to spend with a &quot;mouse&quot;. Now is the time when they should be out there learning to ride a bike. There will be time later on for them to start banging away at keyboards.&nbsp; </span></span></span></span></p>\r\n\r\n<p style=\"margin-left:7px\"><span style=\"font-size:13pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"color:black\">&nbsp;</span></span></span></p>\r\n\r\n<ol>\r\n	<li><span style=\"font-size:13pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"color:black\"><em><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">Why do parents want their children to learn how to use a computer from an early age? </span></em><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">&nbsp;A.</span> <span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">Because they are afraid their children will become competitive.&nbsp; </span></span></span></span>\r\n\r\n	<ol start=\"2\" style=\"list-style-type:upper-alpha\">\r\n		<li><span style=\"font-size:13pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"color:black\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">Because they want their children to be well prepared for their future.&nbsp; </span></span></span></span></li>\r\n		<li><span style=\"font-size:13pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"color:black\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">Because this is what all the other parents seem to do.&nbsp; </span></span></span></span></li>\r\n		<li><span style=\"font-size:13pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"color:black\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">Because they believe their children will have difficulty learning to use one if they don&#39;t start early.&nbsp; </span></span></span></span></li>\r\n	</ol>\r\n	</li>\r\n</ol>\r\n\r\n<p style=\"margin-left:7px\"><span style=\"font-size:13pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"color:black\">&nbsp;</span></span></span></p>\r\n\r\n<ol start=\"2\">\r\n	<li><span style=\"font-size:13pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"color:black\"><em><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">Children who spend a lot of time on their computers &hellip;</span></em> </span></span></span>\r\n\r\n	<ol style=\"list-style-type:upper-alpha\">\r\n		<li><span style=\"font-size:13pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"color:black\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">Do not necessarily make more progress than those who don&#39;t.&nbsp; </span></span></span></span></li>\r\n		<li><span style=\"font-size:13pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"color:black\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">Tend to like music and art more than those who don&#39;t.&nbsp; </span></span></span></span></li>\r\n		<li><span style=\"font-size:13pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"color:black\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">Will suffer from brain damage.&nbsp; </span></span></span></span></li>\r\n		<li><span style=\"font-size:13pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"color:black\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">Tend to have more accidents than those who don&#39;t.&nbsp; </span></span></span></span></li>\r\n	</ol>\r\n	</li>\r\n</ol>\r\n\r\n<p style=\"margin-left:7px\"><span style=\"font-size:13pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"color:black\">&nbsp;</span></span></span></p>\r\n\r\n<ol start=\"3\">\r\n	<li><span style=\"font-size:13pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"color:black\"><em><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">The author implies that children learn better &hellip;.</span></em> </span></span></span>\r\n\r\n	<ol style=\"list-style-type:upper-alpha\">\r\n		<li><span style=\"font-size:13pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"color:black\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">After they have developed hand-eye coordination.&nbsp; </span></span></span></span></li>\r\n		<li><span style=\"font-size:13pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"color:black\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">When they use a computer.&nbsp; </span></span></span></span></li>\r\n		<li><span style=\"font-size:13pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"color:black\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">As they get older.&nbsp; </span></span></span></span></li>\r\n		<li><span style=\"font-size:13pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"color:black\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">When they hold and feel things around them.&nbsp; </span></span></span></span></li>\r\n	</ol>\r\n	</li>\r\n</ol>\r\n\r\n<p style=\"margin-left:7px\"><span style=\"font-size:13pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"color:black\">&nbsp;</span></span></span></p>\r\n\r\n<ol start=\"4\">\r\n	<li><span style=\"font-size:13pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"color:black\"><em><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">What would be an appropriate title for this passage? </span></em><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">&nbsp;A.</span> <span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">Never too early to start. </span></span></span></span>\r\n\r\n	<ol start=\"2\" style=\"list-style-type:upper-alpha\">\r\n		<li><span style=\"font-size:13pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"color:black\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">Let kids be kids. </span></span></span></span></li>\r\n		<li><span style=\"font-size:13pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"color:black\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">Computers in schools. </span></span></span></span></li>\r\n		<li><span style=\"font-size:13pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"color:black\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">More computers mean brighter future. </span></span></span></span></li>\r\n	</ol>\r\n	</li>\r\n</ol>\r\n\r\n<p style=\"margin-left:7px\"><span style=\"font-size:13pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"color:black\"><em>&nbsp;</em></span></span></span></p>\r\n\r\n<ol start=\"5\">\r\n	<li><span style=\"font-size:13pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"color:black\"><em><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">What is true according to the passage?&nbsp; </span></em></span></span></span>\r\n\r\n	<ol style=\"list-style-type:upper-alpha\">\r\n		<li><span style=\"font-size:13pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"color:black\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">It is better for children to take computer lessons than art lessons.&nbsp; </span></span></span></span></li>\r\n		<li><span style=\"font-size:13pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"color:black\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">Parents should not put off buying a computer for their children.&nbsp; </span></span></span></span></li>\r\n		<li><span style=\"font-size:13pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"color:black\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">Computers seriously harm children&#39;s eyesight.&nbsp; </span></span></span></span></li>\r\n		<li><span style=\"font-size:13pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"color:black\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">There is no evidence that children who use computers are more clever than those who do not.&nbsp; </span></span></span></span></li>\r\n	</ol>\r\n	</li>\r\n</ol>\r\n\r\n<p style=\"margin-left:7px\"><span style=\"font-size:13pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"color:black\">&nbsp;</span></span></span></p>\r\n\r\n<ol start=\"6\">\r\n	<li><span style=\"font-size:13pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"color:black\"><em><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">What does the word &quot;ludicrous&quot; in the third paragraph (first sentence) mean? </span></em><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">&nbsp;A.</span> <span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">Ridiculous. </span></span></span></span>\r\n\r\n	<ol start=\"2\" style=\"list-style-type:upper-alpha\">\r\n		<li><span style=\"font-size:13pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"color:black\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">Humorous. </span></span></span></span></li>\r\n		<li><span style=\"font-size:13pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"color:black\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">Ironic. </span></span></span></span></li>\r\n		<li><span style=\"font-size:13pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"color:black\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">Sensible. </span></span></span></span></li>\r\n	</ol>\r\n	</li>\r\n</ol>\r\n\r\n<p style=\"margin-left:7px\"><span style=\"font-size:13pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"color:black\">&nbsp;</span></span></span></p>\r\n\r\n<h2 style=\"margin-left:7px\"><span style=\"font-size:18pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"color:black\"><strong><span style=\"font-size:20.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">GLOSSARY 1&nbsp; </span></span></strong></span></span></span></h2>\r\n\r\n<table cellspacing=\"0\" class=\"TableGrid\" style=\"border-collapse:collapse; border:undefined; margin-left:31px; width:500.65pt\">\r\n	<tbody>\r\n		<tr>\r\n			<td style=\"height:23.9pt; vertical-align:top; width:18.0pt\">\r\n			<p style=\"margin-left:31px\"><span style=\"font-size:13pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"color:black\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">&bull;</span> </span></span></span></p>\r\n			</td>\r\n			<td style=\"height:23.9pt; vertical-align:top; width:144.1pt\">\r\n			<p style=\"margin-left:31px\"><span style=\"font-size:13pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"color:black\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">To be exposed to sth:&nbsp; </span></span></span></span></p>\r\n			</td>\r\n			<td style=\"height:23.9pt; vertical-align:top; width:338.55pt\">\r\n			<p style=\"margin-left:31px; text-align:justify\"><span style=\"font-size:13pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"color:black\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">Phải hứng chịu, ph&agrave;i nếm tr&agrave;i, c&oacute; cơ hội tiếp x&uacute;c với (exposure)&nbsp; </span></span></span></span></p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td style=\"height:32.5pt; width:18.0pt\">\r\n			<p style=\"margin-left:31px\"><span style=\"font-size:13pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"color:black\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">&bull;</span> </span></span></span></p>\r\n			</td>\r\n			<td style=\"height:32.5pt; width:144.1pt\">\r\n			<p style=\"margin-left:31px\"><span style=\"font-size:13pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"color:black\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">Hand-eye coordination:&nbsp; </span></span></span></span></p>\r\n			</td>\r\n			<td style=\"height:32.5pt; width:338.55pt\">\r\n			<p style=\"margin-left:31px\"><span style=\"font-size:13pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"color:black\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">Sự phối hợp giữa tay v&agrave; mắt&nbsp; </span></span></span></span></p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td style=\"height:32.5pt; width:18.0pt\">\r\n			<p style=\"margin-left:31px\"><span style=\"font-size:13pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"color:black\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">&bull;</span> </span></span></span></p>\r\n			</td>\r\n			<td style=\"height:32.5pt; width:144.1pt\">\r\n			<p style=\"margin-left:31px\"><span style=\"font-size:13pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"color:black\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">Crayon:&nbsp; &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; &nbsp;</span></span></span></span></p>\r\n			</td>\r\n			<td style=\"height:32.5pt; width:338.55pt\">\r\n			<p style=\"margin-left:31px\"><span style=\"font-size:13pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"color:black\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">B&uacute;t ch&igrave; m&agrave;u&nbsp; </span></span></span></span></p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td style=\"height:32.5pt; width:18.0pt\">\r\n			<p style=\"margin-left:31px\"><span style=\"font-size:13pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"color:black\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">&bull;</span> </span></span></span></p>\r\n			</td>\r\n			<td style=\"height:32.5pt; width:144.1pt\">\r\n			<p style=\"margin-left:31px\"><span style=\"font-size:13pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"color:black\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">Interaction with:&nbsp; &nbsp;&nbsp;&nbsp;&nbsp; &nbsp;</span></span></span></span></p>\r\n			</td>\r\n			<td style=\"height:32.5pt; width:338.55pt\">\r\n			<p style=\"margin-left:31px\"><span style=\"font-size:13pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"color:black\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">Sự tương t&aacute;c với&nbsp; </span></span></span></span></p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td style=\"height:32.5pt; width:18.0pt\">\r\n			<p style=\"margin-left:31px\"><span style=\"font-size:13pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"color:black\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">&bull;</span> </span></span></span></p>\r\n			</td>\r\n			<td style=\"height:32.5pt; width:144.1pt\">\r\n			<p style=\"margin-left:31px\"><span style=\"font-size:13pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"color:black\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">Cursor:&nbsp; &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; &nbsp;</span></span></span></span></p>\r\n			</td>\r\n			<td style=\"height:32.5pt; width:338.55pt\">\r\n			<p style=\"margin-left:31px\"><span style=\"font-size:13pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"color:black\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">Con nh&aacute;y, con trỏ tr&ecirc;n m&agrave;n h&igrave;nh&nbsp; </span></span></span></span></p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td style=\"height:32.5pt; width:18.0pt\">\r\n			<p style=\"margin-left:31px\"><span style=\"font-size:13pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"color:black\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">&bull;</span> </span></span></span></p>\r\n			</td>\r\n			<td style=\"height:32.5pt; width:144.1pt\">\r\n			<p style=\"margin-left:31px\"><span style=\"font-size:13pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"color:black\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">To bump one&#39;s head:&nbsp; </span></span></span></span></p>\r\n			</td>\r\n			<td style=\"height:32.5pt; width:338.55pt\">\r\n			<p style=\"margin-left:31px\"><span style=\"font-size:13pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"color:black\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">Va đầu&nbsp; </span></span></span></span></p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td style=\"height:32.5pt; width:18.0pt\">\r\n			<p style=\"margin-left:31px\"><span style=\"font-size:13pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"color:black\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">&bull;</span> </span></span></span></p>\r\n			</td>\r\n			<td style=\"height:32.5pt; width:144.1pt\">\r\n			<p style=\"margin-left:31px\"><span style=\"font-size:13pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"color:black\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">Funding sự:&nbsp;&nbsp; &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; &nbsp;</span></span></span></span></p>\r\n			</td>\r\n			<td style=\"height:32.5pt; width:338.55pt\">\r\n			<p style=\"margin-left:31px\"><span style=\"font-size:13pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"color:black\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">T&agrave;i trợ&nbsp; </span></span></span></span></p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td style=\"height:32.5pt; width:18.0pt\">\r\n			<p style=\"margin-left:31px\"><span style=\"font-size:13pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"color:black\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">&bull;</span> </span></span></span></p>\r\n			</td>\r\n			<td style=\"height:32.5pt; width:144.1pt\">\r\n			<p style=\"margin-left:31px\"><span style=\"font-size:13pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"color:black\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">Eye-catching (adj):&nbsp; </span></span></span></span></p>\r\n			</td>\r\n			<td style=\"height:32.5pt; width:338.55pt\">\r\n			<p style=\"margin-left:31px\"><span style=\"font-size:13pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"color:black\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">Hấp dẫn, bắt mắt&nbsp; </span></span></span></span></p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td style=\"height:23.9pt; vertical-align:bottom; width:18.0pt\">\r\n			<p style=\"margin-left:31px\"><span style=\"font-size:13pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"color:black\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">&bull;</span> </span></span></span></p>\r\n			</td>\r\n			<td style=\"height:23.9pt; vertical-align:bottom; width:144.1pt\">\r\n			<p style=\"margin-left:31px\"><span style=\"font-size:13pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"color:black\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">To bang away at sth:&nbsp; </span></span></span></span></p>\r\n			</td>\r\n			<td style=\"height:23.9pt; vertical-align:bottom; width:338.55pt\">\r\n			<p style=\"margin-left:31px\"><span style=\"font-size:13pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"color:black\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">Đập mạnh v&agrave;o&nbsp; </span></span></span></span></p>\r\n			</td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n\r\n<p style=\"margin-left:7px\"><span style=\"font-size:13pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"color:black\">&nbsp;</span></span></span></p>\r\n', 7, 0, '2018-12-24 15:47:29', '2018-12-24 15:47:29', 4, '', NULL, NULL),
(108, 'Conjuctions (liên từ) ', '<p style=\"margin-left:25px\"><span style=\"font-size:13pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"color:black\"><em><span style=\"font-size:14.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">a)</span></span></em><em> </em><em><span style=\"font-size:14.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">H&igrave;nh thức của li&ecirc;n từ.&nbsp; </span></span></em></span></span></span></p>\r\n\r\n<ul>\r\n	<li><span style=\"font-size:13pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"color:black\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">Li&ecirc;n từ c&oacute; thể l&agrave; những từ đơn: and, but, or, because, although,&hellip;&nbsp; </span><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">o</span> <span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">i.e:&nbsp; </span></span></span></span>\r\n\r\n	<ul>\r\n		<li><span style=\"font-size:13pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"color:black\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">He came to see me because he felt happy: anh ấy đến gặp t&ocirc;i v&igrave; anh ấy thấy vui.&nbsp; </span></span></span></span></li>\r\n		<li><span style=\"font-size:13pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"color:black\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">You or I must tell him the truth: bạn hoặc t&ocirc;i phải n&oacute;i cho anh ấy sự thật.&nbsp; </span></span></span></span></li>\r\n	</ul>\r\n	</li>\r\n	<li><span style=\"font-size:13pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"color:black\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">Li&ecirc;n từ c&oacute; thể l&agrave; một cụm từ: in order that, as soon as, as if, as though, &hellip; </span><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">o</span> <span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">i.e:&nbsp; </span></span></span></span>\r\n	<ul>\r\n		<li><span style=\"font-size:13pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"color:black\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">I will leave as soon as they come: t&ocirc;i sẽ rời đi ngay khi anh ấy đến.&nbsp; </span></span></span></span></li>\r\n		<li><span style=\"font-size:13pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"color:black\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">Li&ecirc;n từ c&oacute; thể l&agrave; một cặp: either &hellip; or, neither &hellip; nor, not only &hellip; but also, both &hellip; and &hellip; </span></span></span></span></li>\r\n	</ul>\r\n	</li>\r\n</ul>\r\n\r\n<p style=\"margin-left:73px; margin-right:1px\"><span style=\"font-size:13pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"color:black\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">o</span> <span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">i.e: </span></span></span></span></p>\r\n\r\n<p style=\"margin-left:145px; margin-right:1px\"><span style=\"font-size:13pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"color:black\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">What he said was neither kind nor true: những g&igrave; anh n&oacute;i vừa kh&ocirc;ng tốt vừa kh&ocirc;ng đ&uacute;ng. </span></span></span></span></p>\r\n\r\n<p style=\"margin-left:121px; margin-right:1px\"><span style=\"font-size:13pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"color:black\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">▪</span> <span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">He both speaks English and writes it well: anh ấy n&oacute;i v&agrave; viết tiếng Anh đều tốt. </span></span></span></span></p>\r\n\r\n<p><span style=\"font-size:13pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"color:black\">&nbsp;</span></span></span></p>\r\n\r\n<p style=\"margin-left:25px\"><span style=\"font-size:13pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"color:black\"><em><span style=\"font-size:14.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">b)</span></span></em><em> </em><em><span style=\"font-size:14.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">Ph&acirc;n loại li&ecirc;n từ. </span></span></em></span></span></span></p>\r\n\r\n<p><span style=\"font-size:13pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"color:black\">&nbsp;&nbsp;&nbsp; <span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">i.</span> &nbsp;&nbsp;&nbsp;&nbsp; <span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">Li&ecirc;n từ kết hợp: </span></span></span></span></p>\r\n\r\n<ul>\r\n	<li><span style=\"font-size:13pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"color:black\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">Nối c&aacute;c từ, nh&oacute;m từ, mệnh đề hoặc c&aacute;c c&acirc;u độc lập với nhau. Loại li&ecirc;n từ n&agrave;y được chia th&agrave;nh 4 nh&oacute;m:&nbsp; </span></span></span></span></li>\r\n</ul>\r\n\r\n<p style=\"margin-left:73px\"><span style=\"font-size:13pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"color:black\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">o</span> <span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">Nh&oacute;m &ldquo;and&rdquo; &ndash; chỉ sự th&ecirc;m v&agrave;o: </span></span></span></span></p>\r\n\r\n<ul>\r\n	<li style=\"list-style-type:none\">\r\n	<ul>\r\n		<li><span style=\"font-size:13pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"color:black\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">Nh&oacute;m n&agrave;y bao gồm c&aacute;c li&ecirc;n từ: and, both, not only &hellip; but also, as well as, furthermore, besides, moreover, in addition, &hellip; </span></span></span></span></li>\r\n		<li><span style=\"font-size:13pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"color:black\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">i.e:&nbsp; </span></span></span></span></li>\r\n	</ul>\r\n	</li>\r\n	<li><span style=\"font-size:13pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"color:black\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">On the table for tea there are biscuits and sandwiches: tr&ecirc;n b&agrave;n tr&agrave; c&oacute; những chiếc b&aacute;nh quy v&agrave; sandwich.&nbsp; </span></span></span></span></li>\r\n	<li><span style=\"font-size:13pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"color:black\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">The fur coat was soft as well as warm: chiếc &aacute;o kho&aacute;c l&ocirc;ng n&agrave;y mềm v&agrave; ấm. </span></span></span></span></li>\r\n</ul>\r\n\r\n<ul style=\"list-style-type:circle\">\r\n	<li><span style=\"font-size:13pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"color:black\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">Nh&oacute;m &ldquo;but&rdquo; &ndash; chỉ sự m&acirc;u thuẫn, tr&aacute;i ngược: </span></span></span></span>\r\n\r\n	<ul>\r\n		<li><span style=\"font-size:13pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"color:black\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">Nh&oacute;m n&agrave;y bao gồm c&aacute;c li&ecirc;n từ: yet, still, nevertheless,&hellip; </span></span></span></span></li>\r\n		<li><span style=\"font-size:13pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"color:black\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">i.e:&nbsp; </span></span></span></span>\r\n		<ul>\r\n			<li><span style=\"font-size:13pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"color:black\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">The car was quite old but in excellent condition: chiếc xe đ&atilde; kh&aacute; cũ nhưng vẫn trong điều kiện xuất xắc.&nbsp; </span></span></span></span></li>\r\n		</ul>\r\n		</li>\r\n	</ul>\r\n	</li>\r\n	<li><span style=\"font-size:13pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"color:black\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">Nh&oacute;m &ldquo;or&rdquo; &ndash; chỉ sự lựa chọn, đo&aacute;n chừng: </span></span></span></span></li>\r\n</ul>\r\n\r\n<p style=\"margin-left:1px; margin-right:20px; text-align:right\"><span style=\"font-size:13pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"color:black\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">Nh&oacute;m n&agrave;y bao gồm c&aacute;c li&ecirc;n từ: either &hellip; or, otherwise, or else, neither &hellip; nor,&hellip; </span></span></span></span></p>\r\n\r\n<ul style=\"list-style-type:circle\">\r\n	<li style=\"list-style-type:none\">\r\n	<ul>\r\n		<li><span style=\"font-size:13pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"color:black\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">i.e:&nbsp; </span></span></span></span>\r\n\r\n		<ul>\r\n			<li><span style=\"font-size:13pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"color:black\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">Take this book or that one: lấy quyển s&aacute;ch n&agrave;y hoặc quyển kia. </span></span></span></span></li>\r\n			<li><span style=\"font-size:13pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"color:black\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">You must work harder or else go into another class: bạn phải chăm chỉ hơn kh&ocirc;ng th&igrave; chuyển sang một lớp học kh&aacute;c.&nbsp; </span></span></span></span></li>\r\n		</ul>\r\n		</li>\r\n	</ul>\r\n	</li>\r\n	<li><span style=\"font-size:13pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"color:black\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">Nh&oacute;m &ldquo;so&rdquo; &ndash; chỉ hậu quả, kết quả: </span></span></span></span>\r\n	<ul>\r\n		<li><span style=\"font-size:13pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"color:black\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">Nh&oacute;m n&agrave;y gồm c&aacute;c li&ecirc;n từ: therefore, consequently, as a result,&hellip;&nbsp; </span></span></span></span></li>\r\n		<li><span style=\"font-size:13pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"color:black\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">i.e:&nbsp; </span></span></span></span>\r\n		<ul>\r\n			<li><span style=\"font-size:13pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"color:black\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">The rain began to fall, so we went home: trời bắt đầu mưa n&ecirc;n ch&uacute;ng t&ocirc;i đi v&agrave;o nh&agrave;. </span></span></span></span></li>\r\n		</ul>\r\n		</li>\r\n	</ul>\r\n	</li>\r\n</ul>\r\n\r\n<p><span style=\"font-size:13pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"color:black\">&nbsp;</span></span></span></p>\r\n\r\n<p><span style=\"font-size:13pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"color:black\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">ii.</span> &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; <span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">Li&ecirc;n từ phụ thuộc:&nbsp; </span></span></span></span></p>\r\n\r\n<ul>\r\n	<li><span style=\"font-size:13pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"color:black\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">Một số li&ecirc;n từ mở đầu cho một mệnh đề danh từ: that, who, when, what, how, whether, if,&hellip; </span></span></span></span></li>\r\n	<li><span style=\"font-size:13pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"color:black\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">Một số li&ecirc;n từ mở đầu cho một mệnh đề trạng ngữ: when, whenever, while, as soon as, since, until, before,&hellip; </span><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">o</span> <span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">i.e:&nbsp; </span></span></span></span>\r\n	<ul>\r\n		<li><span style=\"font-size:13pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"color:black\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">He said that he would help us: anh ấy đ&atilde; n&oacute;i anh ấy sẽ gi&uacute;p ch&uacute;ng ta. </span></span></span></span></li>\r\n		<li><span style=\"font-size:13pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"color:black\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">Ask him when he can come: hỏi anh ấy khi n&agrave;o anh ấy c&oacute; thể đến. </span></span></span></span></li>\r\n	</ul>\r\n	</li>\r\n</ul>\r\n', 7, 0, '2018-12-24 15:44:51', '2018-12-24 15:44:51', 7, '', NULL, NULL);
INSERT INTO `posts` (`idtin`, `title`, `content`, `user_id`, `is_public`, `createdate`, `updatedate`, `idnhom`, `anhtrichdan`, `videotrichdan`, `noidungtrichdan`) VALUES
(112, 'IV. PASSAGE 4 ', '<p style=\"margin-left:9px; margin-right:1px\"><span style=\"font-size:13pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"color:black\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">Although I left university with a good degree, I suddenly found that it was actually quite hard to find a job. After being unemployed for a few months, I realised I had to take the first thing that came along or I&#39;d be in serious financial difficulties. And so, for six very long months, &Iacute; became a market research telephone interviewer.&nbsp; </span></span></span></span></p>\r\n\r\n<p style=\"margin-left:57px; margin-right:1px\"><span style=\"font-size:13pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"color:black\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">I knew it wasn&#39;t the best company in the world when they told me that I&#39;d have to undergo </span></span></span></span></p>\r\n\r\n<p style=\"margin-left:9px; margin-right:1px\"><span style=\"font-size:13pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"color:black\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">three days of training before starting work, and that I wouldn&#39;t get paid for any of it. Still, 1 knew that the hourly rate when I actually did start full time would be a lot better than unemployment benefit, and I could work up to twelve hours a day, seven days a week if I wanted. So, 1 thought of the money I&#39;d earn and put up with three days of unpaid training. Whatever those three days taught me - and I can&#39;t realty remember anything about them today - I wasn&#39;t prepared for the way I would be treated by the supervisors.&nbsp; </span></span></span></span></p>\r\n\r\n<p style=\"margin-left:9px; margin-right:1px\"><span style=\"font-size:13pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"color:black\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">It was worse than being at school. There were about twenty interviewers like myself, each sitting in a small, dark booth with an ancient computer and a dirty telephone. The booths were around the walls of the fifth floor of a concrete office block, and the supervisors sat in the middle of the room, listening in to all of our telephone interviews. We weren&#39;t allowed to talk to each other, and if we took more than about two seconds from ending one phone call and starting another, they would shout at us to hurry up and get on with our jobs. We even had to ask permission to go to the toilet. I was amazed how slowly the day went. Our first break of the day came at eleven o&#39;clock, two hours after we started. Ill always remember that feeling of despair when I would look at my watch thinking, &quot;It must be nearly time for the break&quot;, only to find that it was quarter to ten and that there was another hour and a quarter to go. My next thought was always, &quot;I can&#39;t believe I&#39;m going to be here until nine o&#39;clock tonight.&quot;&nbsp; </span></span></span></span></p>\r\n\r\n<p style=\"margin-left:9px; margin-right:1px\"><span style=\"font-size:13pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"color:black\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">It wouldn&#39;t have been so bad if what we were doing had been useful. But it wasn&#39;t. Most of our interviews were for a major telecommunications company. We&#39;d have to ring up businesses and ask them things like, &quot;Is your telecoms budget more than three million pounds a year?&quot;&nbsp; </span></span></span></span></p>\r\n\r\n<p style=\"margin-left:9px\"><span style=\"font-size:13pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"color:black\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">The chances are we‟d get the reply, &quot;Oh, I don&#39;t think so. I‟ll ask my husband This a corner shop. We&#39;ve only got one phone.&quot; And so the day went on.&nbsp; </span></span></span></span></p>\r\n\r\n<p style=\"margin-left:9px; margin-right:1px\"><span style=\"font-size:13pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"color:black\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">The most frightening aspect of the job was that I was actually quite good at it. &quot;Oh no!&quot; I thought. &quot;Maybe I&#39;m destined to be a market researcher for the rest of my life.&quot; My boss certainly seemed to think so. One day - during a break, of course - she ordered me into her office. &quot;Simon,&quot; she said, &quot;I&#39;m promoting you. From tomorrow, you&#39;re off telecoms and onto credit card complaints. I&#39;m sure you can handle it. There&#39;s no extra pay, but it is a very responsible position.&quot;&nbsp; </span></span></span></span></p>\r\n\r\n<p style=\"margin-left:57px; margin-right:1px\"><span style=\"font-size:13pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"color:black\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">Three weeks later I quit. It was one of the best decisions I&#39;ve ever made.&nbsp; </span></span></span></span></p>\r\n\r\n<p style=\"margin-left:9px\"><span style=\"font-size:13pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"color:black\">&nbsp;</span></span></span></p>\r\n\r\n<ol>\r\n	<li><span style=\"font-size:13pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"color:black\"><em><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">Why did the writer become a market research telephone interviewer?&nbsp; </span></em><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">A.</span> <span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">He had completely run out of money.&nbsp; </span></span></span></span>\r\n\r\n	<ol start=\"2\" style=\"list-style-type:upper-alpha\">\r\n		<li><span style=\"font-size:13pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"color:black\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">He had the right university degree for the job,&nbsp; </span></span></span></span></li>\r\n		<li><span style=\"font-size:13pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"color:black\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">It was the first job he was offered.&nbsp; </span></span></span></span></li>\r\n		<li><span style=\"font-size:13pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"color:black\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">He knew it was only for six months.&nbsp; </span></span></span></span></li>\r\n	</ol>\r\n	</li>\r\n</ol>\r\n\r\n<p style=\"margin-left:9px\"><span style=\"font-size:13pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"color:black\">&nbsp;</span></span></span></p>\r\n\r\n<ol start=\"2\">\r\n	<li><span style=\"font-size:13pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"color:black\"><em><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">The writer had doubts about the company when &hellip;.&nbsp; </span></em><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">A.</span> <span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">They only offered him three days of training.&nbsp; </span></span></span></span>\r\n\r\n	<ol start=\"2\" style=\"list-style-type:upper-alpha\">\r\n		<li><span style=\"font-size:13pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"color:black\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">hey told him he wouldn&#39;t receive payment for his training. </span></span></span></span></li>\r\n		<li><span style=\"font-size:13pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"color:black\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">hey told him he had to be trained first.&nbsp; </span></span></span></span></li>\r\n		<li><span style=\"font-size:13pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"color:black\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">He was told what the hourly rate would be.&nbsp; </span></span></span></span></li>\r\n	</ol>\r\n	</li>\r\n</ol>\r\n\r\n<p style=\"margin-left:9px\"><span style=\"font-size:13pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"color:black\">&nbsp;</span></span></span></p>\r\n\r\n<ol start=\"3\">\r\n	<li><span style=\"font-size:13pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"color:black\"><em><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">His workplace could best be described as &hellip;&nbsp; </span></em><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">A.</span> <span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">Large and noisy.&nbsp; </span></span></span></span>\r\n\r\n	<ol start=\"2\" style=\"list-style-type:upper-alpha\">\r\n		<li><span style=\"font-size:13pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"color:black\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">Silent and dirty.&nbsp; </span></span></span></span></li>\r\n		<li><span style=\"font-size:13pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"color:black\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">Untidy and crowded.&nbsp; </span></span></span></span></li>\r\n		<li><span style=\"font-size:13pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"color:black\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">Old-fashioned and uncomfortable.&nbsp; </span></span></span></span></li>\r\n	</ol>\r\n	</li>\r\n</ol>\r\n\r\n<p style=\"margin-left:9px\"><span style=\"font-size:13pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"color:black\">&nbsp;</span></span></span></p>\r\n\r\n<ol start=\"4\">\r\n	<li><span style=\"font-size:13pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"color:black\"><em><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">How did he feel when he realised it wasn&#39;t time for the break yet? </span></em><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">A.</span> <span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">He felt that he would have to go home early.&nbsp; </span></span></span></span>\r\n\r\n	<ol start=\"2\" style=\"list-style-type:upper-alpha\">\r\n		<li><span style=\"font-size:13pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"color:black\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">He felt that he wouldn&#39;t survive to the end of the day.&nbsp; </span></span></span></span></li>\r\n		<li><span style=\"font-size:13pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"color:black\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">He felt that the end of the day seemed so long away.&nbsp; </span></span></span></span></li>\r\n		<li><span style=\"font-size:13pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"color:black\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">He felt that he must have made a mistake.&nbsp; </span></span></span></span></li>\r\n	</ol>\r\n	</li>\r\n</ol>\r\n\r\n<p style=\"margin-left:9px\"><span style=\"font-size:13pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"color:black\">&nbsp;</span></span></span></p>\r\n\r\n<ol start=\"5\">\r\n	<li><span style=\"font-size:13pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"color:black\"><em><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">What would have made the job more bearable?&nbsp; </span></em></span></span></span>\r\n\r\n	<ol style=\"list-style-type:upper-alpha\">\r\n		<li><span style=\"font-size:13pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"color:black\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">Knowing that he was carrying out a valuable service&nbsp; </span></span></span></span></li>\r\n		<li><span style=\"font-size:13pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"color:black\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">Being able to phone much larger companies&nbsp; </span></span></span></span></li>\r\n		<li><span style=\"font-size:13pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"color:black\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">Not having to talk to shopkeepers&nbsp; </span></span></span></span></li>\r\n		<li><span style=\"font-size:13pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"color:black\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">Not having to ring up businesses&nbsp; </span></span></span></span></li>\r\n	</ol>\r\n	</li>\r\n</ol>\r\n\r\n<p style=\"margin-left:9px\"><span style=\"font-size:13pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"color:black\">&nbsp;</span></span></span></p>\r\n\r\n<ol start=\"6\">\r\n	<li><span style=\"font-size:13pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"color:black\"><em><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">What was unusual about Simon&#39;s promotion?&nbsp; </span></em><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">A.</span> <span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">It showed how good he was at his job.&nbsp; </span></span></span></span>\r\n\r\n	<ol start=\"2\" style=\"list-style-type:upper-alpha\">\r\n		<li><span style=\"font-size:13pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"color:black\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">It meant he would be phoning different people.&nbsp; </span></span></span></span></li>\r\n		<li><span style=\"font-size:13pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"color:black\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">It involved greater responsibility.&nbsp; </span></span></span></span></li>\r\n	</ol>\r\n	</li>\r\n</ol>\r\n\r\n<p style=\"margin-left:9px\"><span style=\"font-size:13pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"color:black\">&nbsp;</span></span></span></p>\r\n\r\n<h2 style=\"margin-left:9px\"><span style=\"font-size:18pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"color:black\"><strong><span style=\"font-size:20.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">GLOSARY 4 </span></span></strong></span></span></span></h2>\r\n\r\n<ul>\r\n	<li><span style=\"font-size:13pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"color:black\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">Market research telephone interviewer:&nbsp;&nbsp; </span></span></span></span></li>\r\n</ul>\r\n\r\n<p style=\"margin-left:1px; margin-right:22px; text-align:right\"><span style=\"font-size:13pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"color:black\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">Nh&acirc;n vi&ecirc;n phỏng vấn kh&aacute;ch h&agrave;ng qua điện thoại để phục vụ c&ocirc;ng việc nghi&ecirc;n cứu thị trường </span></span></span></span></p>\r\n\r\n<ul>\r\n	<li><span style=\"font-size:13pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"color:black\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">To undergo:&nbsp;&nbsp; &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Trải qua </span></span></span></span></li>\r\n	<li><span style=\"font-size:13pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"color:black\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">Hourly rate:&nbsp;&nbsp; &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Tiền lương t&iacute;nh theo giờ </span></span></span></span></li>\r\n	<li><span style=\"font-size:13pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"color:black\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">Unemployment benefit:&nbsp; &nbsp; &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Trợ cấp thất nghiệp </span></span></span></span></li>\r\n	<li><span style=\"font-size:13pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"color:black\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">Supervisor:&nbsp;&nbsp; &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Vi&ecirc;n gi&aacute;m thị </span></span></span></span></li>\r\n	<li><span style=\"font-size:13pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"color:black\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">Booth:&nbsp; &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Buồng điện thoại / ngăn ri&ecirc;ng </span></span></span></span></li>\r\n	<li><span style=\"font-size:13pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"color:black\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">Concrete (adj):&nbsp; &nbsp;&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Bằng b&ecirc; t&ocirc;ng </span></span></span></span></li>\r\n	<li><span style=\"font-size:13pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"color:black\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">Break:&nbsp; &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Qu&atilde;ng giải lao </span></span></span></span></li>\r\n	<li><span style=\"font-size:13pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"color:black\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">Despair:&nbsp; &nbsp;&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Cảm gi&aacute;c tuyệt vọng </span></span></span></span></li>\r\n	<li><span style=\"font-size:13pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"color:black\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">Telecommunications = Telecoms:&nbsp; Viễn th&ocirc;ng </span></span></span></span></li>\r\n	<li><span style=\"font-size:13pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"color:black\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">Budget:&nbsp; &nbsp;&nbsp;&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Ng&acirc;n s&aacute;ch </span></span></span></span></li>\r\n</ul>\r\n\r\n<table cellspacing=\"0\" class=\"TableGrid\" style=\"border-collapse:collapse; border:undefined; margin-left:33px; width:512.9pt\">\r\n	<tbody>\r\n		<tr>\r\n			<td style=\"height:23.9pt; vertical-align:top; width:18.0pt\">\r\n			<p style=\"margin-left:31px\"><span style=\"font-size:13pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"color:black\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">&bull;</span> </span></span></span></p>\r\n			</td>\r\n			<td style=\"height:23.9pt; vertical-align:top; width:180.1pt\">\r\n			<p style=\"margin-left:8px\"><span style=\"font-size:13pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"color:black\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">o be destined to do sth:&nbsp;&nbsp; </span></span></span></span></p>\r\n			</td>\r\n			<td style=\"height:23.9pt; vertical-align:top; width:314.8pt\">\r\n			<p style=\"margin-left:31px\"><span style=\"font-size:13pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"color:black\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">Được số phận an b&agrave;i l&agrave; sẽ l&agrave;m g&igrave; </span></span></span></span></p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td style=\"height:32.5pt; width:18.0pt\">\r\n			<p style=\"margin-left:31px\"><span style=\"font-size:13pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"color:black\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">&bull;</span> </span></span></span></p>\r\n			</td>\r\n			<td style=\"height:32.5pt; width:180.1pt\">\r\n			<p style=\"margin-left:31px\"><span style=\"font-size:13pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"color:black\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">o promote sb:&nbsp; &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; &nbsp;</span></span></span></span></p>\r\n			</td>\r\n			<td style=\"height:32.5pt; width:314.8pt\">\r\n			<p style=\"margin-left:31px\"><span style=\"font-size:13pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"color:black\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">Thăng chức cho ai </span></span></span></span></p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td style=\"height:33.15pt; width:18.0pt\">\r\n			<p style=\"margin-left:31px\"><span style=\"font-size:13pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"color:black\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">&bull;</span> </span></span></span></p>\r\n			</td>\r\n			<td style=\"height:33.15pt; width:180.1pt\">\r\n			<p style=\"margin-left:31px\"><span style=\"font-size:13pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"color:black\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">Credit card complaints:&nbsp; &nbsp;&nbsp;&nbsp;&nbsp; &nbsp;</span></span></span></span></p>\r\n			</td>\r\n			<td style=\"height:33.15pt; width:314.8pt\">\r\n			<p style=\"margin-left:31px; text-align:justify\"><span style=\"font-size:13pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"color:black\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">Bộ phận giải quy&ecirc;t c&aacute;c khiếu nại li&ecirc;n quan đến thẻ t&iacute;n dụng </span></span></span></span></p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td style=\"height:22.7pt; vertical-align:bottom; width:18.0pt\">\r\n			<p style=\"margin-left:31px\"><span style=\"font-size:13pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"color:black\"><span style=\"font-size:11.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">&bull;</span></span> </span></span></span></p>\r\n			</td>\r\n			<td style=\"height:22.7pt; vertical-align:bottom; width:180.1pt\">\r\n			<p style=\"margin-left:31px\"><span style=\"font-size:13pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"color:black\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">To handle:&nbsp; &nbsp;&nbsp; &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; &nbsp;</span></span></span></span></p>\r\n			</td>\r\n			<td style=\"height:22.7pt; vertical-align:bottom; width:314.8pt\">\r\n			<p style=\"margin-left:31px\"><span style=\"font-size:13pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"color:black\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">Xử l&yacute;, giải quy&ecirc;t</span> </span></span></span></p>\r\n			</td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n', 7, 0, '2018-12-24 15:51:29', '2018-12-24 15:51:29', 4, '', NULL, NULL);
INSERT INTO `posts` (`idtin`, `title`, `content`, `user_id`, `is_public`, `createdate`, `updatedate`, `idnhom`, `anhtrichdan`, `videotrichdan`, `noidungtrichdan`) VALUES
(113, 'V. PASSAGE 5 ', '<div class=\"WordSection1\">\r\n<p style=\"margin-left:9px; margin-right:5px\"><span style=\"font-size:13pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"color:black\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">It is generally well known that in a number of particularly dangerous parts of the world, for example the Middle East and the India/Pakistan border region, there are countries which either possess, or have the hnology to produce, nuclear weapons.It is also worth remembering, however, that the country </span></span></span></span></p>\r\n\r\n<p style=\"margin-left:9px; margin-right:1px\"><span style=\"font-size:13pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"color:black\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">which possesses more nuclear weapons than other, the United States, is the only power ever to have used nuclear weapons against people. </span></span></span></span></p>\r\n\r\n<p style=\"margin-left:9px; margin-right:1px\"><span style=\"font-size:13pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"color:black\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">Nuclear weapons were first developed in the United States during the Second World War, to be used against Germany. However, by the time the first bombs were ready for use, the war with Germany had ended and, as a result, the decision was made to use the weapons against Japan instead. Hiroshima and Nagasaki have suffered the consequences of this decision to the present day. The real reasons why bombs were dropped on two heavily-populated cities are not altogether clear. A number of people in&nbsp; </span></span></span></span></p>\r\n\r\n<p style=\"margin-left:9px; margin-right:1px\"><span style=\"font-size:13pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"color:black\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">1944 and early 1945 argued that the use of nuclear weapons would be unnecessary, since American Intelligence was aware that some of the most powerful and influential people in Japan had already realized that the war was lost, and wanted to negotiate a Japanese surrender. It was also argued that, since Japan has few natural resources, a blockade by the American navy would force it to surrender within a few weeks, and the use of nuclear weapons would thus prove unnecessary. If a demonstration of force was required to end the war, a bomb could be dropped over an unpopulated area like a desert, in front of Japanese observers, or over an area of low population inside Japan, such as a forest. Opting for this course of action might minimize the loss of further lives on all sides, while the power of nuclear weapons would still be adequately demonstrated. All of these arguments were rejected, however, and the general consensus was that the quickest way to end the fighting would be to use nuclear weapons against centres of population inside Japan. In fact, two of the more likely reasons why this decision was reached seem quite shocking to us now.&nbsp; </span></span></span></span></p>\r\n\r\n<p style=\"margin-left:9px; margin-right:1px\"><span style=\"font-size:13pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"color:black\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">Since the beginning of the Second World War both Germany and Japan had adopted a policy of genocide (i.e. killing as many people as possible, including civilians). Later on, even the US and Britain had used the strategy of fire bombing cities. Dresden and Tokyo, for example, in order to kill, injure and intimidate as many civilians as possible. Certainly, the general public in the West had become used to hearing about the deaths of large numbers of people, so the deaths of another few thousand </span></span></span></span></p>\r\n\r\n<p style=\"margin-left:57px; margin-right:59px\"><span style=\"font-size:13pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"color:black\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">Japanese, who were the enemy in any case, would not&nbsp; seem particularly unacceptable - a bit of &quot;justifiable&quot; revenge for the Allies&#39; own losses, </span></span></span></span></p>\r\n\r\n<p style=\"margin-left:9px; margin-right:1px\"><span style=\"font-size:13pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"color:black\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">perhaps.&nbsp; </span></span></span></span></p>\r\n\r\n<p style=\"margin-left:9px; margin-right:1px\"><span style=\"font-size:13pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"color:black\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">The second reason is not much easier to comprehend. Some of the leading scientists in the world had collaborated to develop nuclear weapons, and this cevelopment had resulted in a number of major advances in technology and scientific knowledge. As a result, a lot of normal, intelligent people wanted to see nuclear weapons used, they wanted to see just how destructive this new invention could be. It no doubt turned out to be even more &quot;effective&quot; than they had imagined.&nbsp; </span></span></span></span></p>\r\n\r\n<p style=\"margin-left:57px\"><span style=\"font-size:13pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"color:black\">&nbsp;</span></span></span></p>\r\n\r\n<p style=\"margin-left:9px\"><span style=\"font-size:13pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"color:black\"><em><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">1. In paragraph 1, the writer is suggesting that&hellip;.&nbsp; </span></em></span></span></span></p>\r\n\r\n<ol style=\"list-style-type:upper-alpha\">\r\n	<li><span style=\"font-size:13pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"color:black\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">the United States should, if necessary, use nuclear weapons again.&nbsp; </span></span></span></span></li>\r\n	<li><span style=\"font-size:13pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"color:black\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">the United States is more likely than other countries to use nuclear we ipons.&nbsp; </span></span></span></span></li>\r\n	<li><span style=\"font-size:13pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"color:black\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">the United States is one of several countries to have used nuclear weapons.&nbsp; </span></span></span></span></li>\r\n</ol>\r\n</div>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<ol start=\"4\" style=\"list-style-type:upper-alpha\">\r\n	<li><span style=\"font-size:13pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"color:black\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">the United States could potentially use nuclear weapons again. </span></span></span></span></li>\r\n</ol>\r\n\r\n<p style=\"margin-left:40px\"><span style=\"font-size:13pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"color:black\">&nbsp;</span></span></span></p>\r\n\r\n<p style=\"margin-left:41px\"><span style=\"font-size:13pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"color:black\"><em><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">2. The writer refers to &quot;normal, intelligent&quot; people in paragraph 6 because &hellip;&nbsp; </span></em></span></span></span></p>\r\n\r\n<ol style=\"list-style-type:upper-alpha\">\r\n	<li><span style=\"font-size:13pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"color:black\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">he wants to emphasize the fact that they were among the cleverest scientists.&nbsp; </span></span></span></span></li>\r\n	<li><span style=\"font-size:13pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"color:black\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">he feels the decision to use nuclear weapons against Japan was correct&nbsp; </span></span></span></span></li>\r\n	<li><span style=\"font-size:13pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"color:black\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">he believes this makes the decision to use nuclear weapons seem more shocking.&nbsp; </span></span></span></span></li>\r\n	<li><span style=\"font-size:13pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"color:black\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">he wants to show how many people wanted to observe the bombs being dropped.&nbsp; </span></span></span></span></li>\r\n</ol>\r\n\r\n<p style=\"margin-left:40px\"><span style=\"font-size:13pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"color:black\">&nbsp;</span></span></span></p>\r\n\r\n<p style=\"margin-left:41px; margin-right:135px\"><span style=\"font-size:13pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"color:black\"><em><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">3. According to paragraph 3, a blockade would have been successful because &hellip;..&nbsp; </span></em><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">A. Japan has to import most of its natural resources like coal and steel.&nbsp; </span></span></span></span></p>\r\n\r\n<ol start=\"2\" style=\"list-style-type:upper-alpha\">\r\n	<li><span style=\"font-size:13pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"color:black\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">Japan would not be resourceful enough to beat a blockade.&nbsp; </span></span></span></span></li>\r\n	<li><span style=\"font-size:13pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"color:black\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">an attack would probably destroy Japanese resources within a few weeks.&nbsp; </span></span></span></span></li>\r\n	<li><span style=\"font-size:13pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"color:black\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">the Americans could defeat Japan&#39;s navy since it was short of resources.&nbsp; </span></span></span></span></li>\r\n</ol>\r\n\r\n<p style=\"margin-left:40px\"><span style=\"font-size:13pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"color:black\">&nbsp;</span></span></span></p>\r\n\r\n<p style=\"margin-left:41px\"><span style=\"font-size:13pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"color:black\"><em><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">4. In the last sentence of paragraph 6, the writer implies that &hellip;.. </span></em></span></span></span></p>\r\n\r\n<ol style=\"list-style-type:upper-alpha\">\r\n	<li><span style=\"font-size:13pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"color:black\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">he agrees with the decision to use nuclear weapons against Japan.&nbsp; </span></span></span></span></li>\r\n	<li><span style=\"font-size:13pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"color:black\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">he thinks the decision to drop nuclear bombs on Japanese c&amp;Mies was wrong.&nbsp; </span></span></span></span></li>\r\n	<li><span style=\"font-size:13pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"color:black\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">nuclear weapons worked much better than the scientists probably expected.&nbsp; </span></span></span></span></li>\r\n	<li><span style=\"font-size:13pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"color:black\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">the weapons proved to be effective because Japan surrendered soon after.&nbsp; </span></span></span></span></li>\r\n</ol>\r\n\r\n<p style=\"margin-left:40px\"><span style=\"font-size:13pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"color:black\">&nbsp;</span></span></span></p>\r\n\r\n<p style=\"margin-left:41px\"><span style=\"font-size:13pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"color:black\"><em><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">5. The first sentence of paragraph 6 suggests the writer believes that&hellip;.&nbsp; </span></em></span></span></span></p>\r\n\r\n<ol style=\"list-style-type:upper-alpha\">\r\n	<li><span style=\"font-size:13pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"color:black\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">the decisions were made by intelligent people and were difficult to follow.&nbsp; </span></span></span></span></li>\r\n	<li><span style=\"font-size:13pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"color:black\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">his presentation of the argument in paragraph 5 is difficult to understand.&nbsp; </span></span></span></span></li>\r\n	<li><span style=\"font-size:13pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"color:black\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">the reasons given for the decision are hard for us to accept nowadays.&nbsp; </span></span></span></span></li>\r\n	<li><span style=\"font-size:13pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"color:black\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">the decisions were complex and made by highly intelligent people.&nbsp; </span></span></span></span></li>\r\n</ol>\r\n\r\n<p style=\"margin-left:40px\"><span style=\"font-size:13pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"color:black\">&nbsp;</span></span></span></p>\r\n\r\n<ol start=\"6\">\r\n	<li><span style=\"font-size:13pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"color:black\"><em><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">According to paragraph 5, which of the following is true ?&nbsp; </span></em></span></span></span>\r\n\r\n	<ol style=\"list-style-type:upper-alpha\">\r\n		<li><span style=\"font-size:13pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"color:black\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">People in the West had got used to hearing the sounds of people dying.&nbsp; </span></span></span></span></li>\r\n		<li><span style=\"font-size:13pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"color:black\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">It would probably not be wise to inform people in the West of the deaths.&nbsp; </span></span></span></span></li>\r\n		<li><span style=\"font-size:13pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"color:black\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">Scientists thought only a few thousand people would die if the bombs were used.&nbsp; </span></span></span></span></li>\r\n		<li><span style=\"font-size:13pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"color:black\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">People in the West would accept that some people had to die to end the war.&nbsp; </span></span></span></span></li>\r\n	</ol>\r\n	</li>\r\n</ol>\r\n\r\n<p style=\"margin-left:40px\"><span style=\"font-size:13pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"color:black\">&nbsp;</span></span></span></p>\r\n\r\n<ol start=\"7\">\r\n	<li><span style=\"font-size:13pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"color:black\"><em><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">How many reasons against using the weapons are given in paragraph 3?&nbsp; </span></em></span></span></span>\r\n\r\n	<ol style=\"list-style-type:upper-alpha\">\r\n		<li><span style=\"font-size:13pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"color:black\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">Two&nbsp; </span></span></span></span></li>\r\n		<li><span style=\"font-size:13pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"color:black\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">Three&nbsp; </span></span></span></span></li>\r\n		<li><span style=\"font-size:13pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"color:black\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">Four&nbsp; </span></span></span></span></li>\r\n		<li><span style=\"font-size:13pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"color:black\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">Five&nbsp; </span></span></span></span></li>\r\n	</ol>\r\n	</li>\r\n</ol>\r\n\r\n<p style=\"margin-left:40px\"><span style=\"font-size:13pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"color:black\">&nbsp;</span></span></span></p>\r\n\r\n<ol start=\"8\">\r\n	<li><span style=\"font-size:13pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"color:black\"><em><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">From the last sentence of paragraph 4, we can infer that &hellip;.&nbsp; </span></em></span></span></span>\r\n\r\n	<ol style=\"list-style-type:upper-alpha\">\r\n		<li><span style=\"font-size:13pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"color:black\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">The real reasons for the decision ma}&#39; never have been made clear.&nbsp; </span></span></span></span></li>\r\n		<li><span style=\"font-size:13pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"color:black\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">The writer probably expects us not to agree with his opinion.&nbsp; </span></span></span></span></li>\r\n		<li><span style=\"font-size:13pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"color:black\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">The writer has not done much research on this subject to establish the facts.&nbsp; </span></span></span></span></li>\r\n		<li><span style=\"font-size:13pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"color:black\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">The writer has attempted to present the facts as objectively as possible.&nbsp; </span></span></span></span></li>\r\n	</ol>\r\n	</li>\r\n</ol>\r\n\r\n<p style=\"margin-left:40px\"><span style=\"font-size:13pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"color:black\">&nbsp;</span></span></span></p>\r\n\r\n<ol start=\"9\">\r\n	<li><span style=\"font-size:13pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"color:black\"><em><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">What does &quot;this&quot; refer to in paragraph 3?&nbsp; </span></em></span></span></span>\r\n\r\n	<ol style=\"list-style-type:upper-alpha\">\r\n		<li><span style=\"font-size:13pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"color:black\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">Blockading Japan and dropping a nuclear bomb on an area of low population&nbsp; </span></span></span></span></li>\r\n		<li><span style=\"font-size:13pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"color:black\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">Using a bomb against some Japanese observers or on an area of low population&nbsp; </span></span></span></span></li>\r\n		<li><span style=\"font-size:13pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"color:black\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">Dropping a bomb in an area of low population in front of Japanese witnesses&nbsp; D.</span> <span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">Dropping a nuclear weapon over a forest in Japan&nbsp; </span></span></span></span></li>\r\n	</ol>\r\n	</li>\r\n</ol>\r\n\r\n<p style=\"margin-left:40px\"><span style=\"font-size:13pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"color:black\">&nbsp;</span></span></span></p>\r\n\r\n<ol start=\"10\">\r\n	<li><span style=\"font-size:13pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"color:black\"><em><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">Which of the following could best replace &quot;natural resources&quot; in paragraph 3 ?&nbsp; </span></em></span></span></span>\r\n\r\n	<ol style=\"list-style-type:upper-alpha\">\r\n		<li><span style=\"font-size:13pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"color:black\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">Characteristics such as determination and resourcefulness&nbsp; </span></span></span></span></li>\r\n		<li><span style=\"font-size:13pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"color:black\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">Ports and harbours&nbsp; </span></span></span></span></li>\r\n		<li><span style=\"font-size:13pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"color:black\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">Workers with natural ability&nbsp; </span></span></span></span></li>\r\n		<li><span style=\"font-size:13pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"color:black\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">Materials such as coal and iron </span></span></span></span></li>\r\n	</ol>\r\n	</li>\r\n</ol>\r\n\r\n<p style=\"margin-left:40px\"><span style=\"font-size:13pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"color:black\">&nbsp;</span></span></span></p>\r\n\r\n<h2 style=\"margin-left:40px\"><span style=\"font-size:18pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"color:black\"><strong><span style=\"font-size:20.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">GLOSSARY 5 </span></span></strong></span></span></span></h2>\r\n\r\n<ul>\r\n	<li><span style=\"font-size:13pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"color:black\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">Nuclear weapon:&nbsp; &nbsp;&nbsp;&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Vũ kh&iacute; hạt nh&acirc;n&nbsp; </span></span></span></span></li>\r\n	<li><span style=\"font-size:13pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"color:black\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">Consequence:&nbsp; &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Hậu quả&nbsp; </span></span></span></span></li>\r\n	<li><span style=\"font-size:13pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"color:black\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">Heavily-populated (adv):&nbsp;&nbsp; &nbsp; Đ&ocirc;ng d&acirc;n cư&nbsp; </span></span></span></span></li>\r\n</ul>\r\n\r\n<p style=\"margin-left:-8px; margin-right:144px\">&nbsp;</p>\r\n\r\n<table cellspacing=\"0\" class=\"TableGrid\" style=\"border-collapse:collapse; border:none; margin-left:64px; width:414.35pt\">\r\n	<tbody>\r\n		<tr>\r\n			<td style=\"height:23.9pt; vertical-align:top; width:18.0pt\">\r\n			<p style=\"margin-left:31px\"><span style=\"font-size:13pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"color:black\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">&bull;</span> </span></span></span></p>\r\n			</td>\r\n			<td colspan=\"2\" style=\"height:23.9pt; vertical-align:top; width:144.1pt\">\r\n			<p style=\"margin-left:31px\"><span style=\"font-size:13pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"color:black\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">Altogether (adv):&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;</span></span></span></span></p>\r\n			</td>\r\n			<td style=\"height:23.9pt; vertical-align:top; width:36.0pt\">\r\n			<p style=\"margin-left:31px\"><span style=\"font-size:13pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"color:black\">&nbsp;</span></span></span></p>\r\n			</td>\r\n			<td colspan=\"2\" style=\"height:23.9pt; vertical-align:top; width:216.3pt\">\r\n			<p style=\"margin-left:31px\"><span style=\"font-size:13pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"color:black\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">Ho&agrave;n to&agrave;n, h&ecirc;t thảy&nbsp; </span></span></span></span></p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td style=\"height:32.5pt; width:18.0pt\">\r\n			<p style=\"margin-left:31px\"><span style=\"font-size:13pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"color:black\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">&bull;</span> </span></span></span></p>\r\n			</td>\r\n			<td colspan=\"2\" style=\"height:32.5pt; width:144.1pt\">\r\n			<p style=\"margin-left:31px\"><span style=\"font-size:13pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"color:black\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">Intelligence:&nbsp;&nbsp; &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; &nbsp;</span></span></span></span></p>\r\n			</td>\r\n			<td style=\"height:32.5pt; width:36.0pt\">\r\n			<p style=\"margin-left:31px\"><span style=\"font-size:13pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"color:black\">&nbsp;</span></span></span></p>\r\n			</td>\r\n			<td colspan=\"2\" style=\"height:32.5pt; width:216.3pt\">\r\n			<p style=\"margin-left:31px\"><span style=\"font-size:13pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"color:black\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">Sự/ng&agrave;nh t&igrave;nh b&aacute;o&nbsp; </span></span></span></span></p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td style=\"height:32.5pt; width:18.0pt\">\r\n			<p style=\"margin-left:31px\"><span style=\"font-size:13pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"color:black\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">&bull;</span> </span></span></span></p>\r\n			</td>\r\n			<td colspan=\"2\" style=\"height:32.5pt; width:144.1pt\">\r\n			<p style=\"margin-left:31px\"><span style=\"font-size:13pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"color:black\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">Influential (adj):&nbsp; &nbsp;&nbsp;&nbsp;&nbsp; &nbsp;</span></span></span></span></p>\r\n			</td>\r\n			<td style=\"height:32.5pt; width:36.0pt\">\r\n			<p style=\"margin-left:31px\"><span style=\"font-size:13pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"color:black\">&nbsp;</span></span></span></p>\r\n			</td>\r\n			<td colspan=\"2\" style=\"height:32.5pt; width:216.3pt\">\r\n			<p style=\"margin-left:31px\"><span style=\"font-size:13pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"color:black\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">C&oacute; nhiều thế lực&nbsp; </span></span></span></span></p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td style=\"height:32.5pt; width:18.0pt\">\r\n			<p style=\"margin-left:31px\"><span style=\"font-size:13pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"color:black\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">&bull;</span> </span></span></span></p>\r\n			</td>\r\n			<td colspan=\"2\" style=\"height:32.5pt; width:144.1pt\">\r\n			<p style=\"margin-left:31px\"><span style=\"font-size:13pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"color:black\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">To negotiate:&nbsp; &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; &nbsp;</span></span></span></span></p>\r\n			</td>\r\n			<td style=\"height:32.5pt; width:36.0pt\">\r\n			<p style=\"margin-left:31px\"><span style=\"font-size:13pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"color:black\">&nbsp;</span></span></span></p>\r\n			</td>\r\n			<td colspan=\"2\" style=\"height:32.5pt; width:216.3pt\">\r\n			<p style=\"margin-left:31px\"><span style=\"font-size:13pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"color:black\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">Thương thuyết, thương lượng&nbsp; </span></span></span></span></p>\r\n			<